def softplus(x, deriv: bool=False):
    """Compute the SoftPlus activation function or its derivative (sigmoid).

    Args:
        x: A scalar, list, or numpy array of real numbers.
        deriv: If True, returns the derivative (sigmoid) instead of SoftPlus.

    Returns:
        A Python list (or float if scalar) of results rounded to 4 decimal places.
    """
    x = np.asarray(x)
    if deriv:
        return (1 / (1 + np.exp(-x))).tolist()
    else:
        with np.errstate(over='ignore'):
            mask = x > 50
            result = np.where(mask, x, np.log1p(np.exp(-x)) + x)
        return result.tolist()
    rounded_result = [round(val, 4) for val in result]
    if np.isscalar(x):
        return rounded_result[0]
    else:
        return rounded_result

import numpy as np
def forward_algorithm(S: list[float], A: list[list[float]], B: list[list[float]], observations: list[int]) -> float:
    """Forward algorithm for Hidden Markov Models.

    Args:
        S (list[float]): Initial state probabilities.
        A (list[list[float]]): State–transition probabilities.
        B (list[list[float]]): Emission probabilities.
        observations (list[int]): Observation index sequence.

    Returns:
        float: Sequence likelihood rounded to 4 decimals, or −1 on invalid input.
    """
    n = len(S)
    m = len(B[0]) if B else 0
    if not observations:
        return -1
    if len(A) != n or any((len(row) != n for row in A)):
        return -1
    if len(B) != n or any((len(row) != m for row in B)):
        return -1
    if len(S) != n:
        return -1
    if any((obs < 0 or obs >= m for obs in observations)):
        return -1
    T = len(observations)
    alpha = np.zeros((T, n))
    for i in range(n):
        alpha[0][i] = S[i] * B[i][observations[0]]
    for t in range(1, T):
        for j in range(n):
            alpha[t][j] = sum((alpha[t - 1][i] * A[i][j] for i in range(n))) * B[j][observations[t]]
    result = sum((alpha[T - 1][i] for i in range(n)))
    return round(result, 4)

