def fp_growth(transactions: List[List[str]], min_support: int) -> List[List[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth."""
    if not transactions:
        return []
    header_table = Counter()
    for transaction in transactions:
        header_table.update(transaction)
    frequent_items = {item for (item, count) in header_table.items() if count >= min_support}
    if not frequent_items:
        return []
    filtered_transactions = []
    for transaction in transactions:
        filtered_transaction = [item for item in transaction if item in frequent_items]
        if filtered_transaction:
            filtered_transactions.append(sorted(filtered_transaction))
    sorted_items = sorted(frequent_items, key=lambda x: (-header_table[x], x))
    sorted_item_map = {item: idx for (idx, item) in enumerate(sorted_items)}

    def sort_transaction(transaction: List[str]) -> List[str]:
        return sorted(transaction, key=lambda x: (-header_table[x], sorted_item_map[x]))
    sorted_transactions = [sort_transaction(transaction) for transaction in filtered_transactions]

    def create_fp_tree(transactions: List[List[str]], header_table: Counter) -> Tuple[FPTreeNode, Dict[str, List[FPTreeNode]]]:
        root = FPTreeNode('Null', 1, None)
        for item in header_table:
            header_table[item] = [header_table[item], None]
        for transaction in transactions:
            if transaction:
                insert_tree(transaction, root, header_table)
        return (root, header_table)

    def insert_tree(items: List[str], node: FPTreeNode, header_table: Dict[str, List]):
        first = items[0]
        if first in node.children:
            node.children[first].count += 1
        else:
            node.children[first] = FPTreeNode(first, 1, node)
            update_header(header_table[first], node.children[first])
        rest = items[1:]
        if rest:
            next_node = node.children[first]
            insert_tree(rest, next_node, header_table)

    def update_header(item_entry: List, target_node: FPTreeNode):
        if item_entry[1] is None:
            item_entry[1] = target_node
        else:
            while item_entry[1].node_link is not None:
                item_entry[1] = item_entry[1].node_link
            item_entry[1].node_link = target_node
    (fp_tree, header_table) = create_fp_tree(sorted_transactions, header_table.copy())

    def mine_tree(header_table: Dict[str, List], tree: FPTreeNode, prefix: List[str], frequent_itemsets: List[List[str]]):
        sorted_items = [item for item in header_table if header_table[item][0] >= min_support]
        sorted_items.sort(key=lambda x: header_table[x][0])
        for item in sorted_items:
            new_prefix = sorted(prefix + [item])
            frequent_itemsets.append(new_prefix)
            conditional_pattern_base = []
            node = header_table[item][1]
            while node is not None:
                path = []
                count = node.count
                current = node.parent
                while current.name != 'Null':
                    path.append(current.name)
                    current = current.parent
                if path:
                    conditional_pattern_base.append((path[::-1], count))
                node = node.node_link
            conditional_header_table = Counter()
            for (pattern, count) in conditional_pattern_base:
                conditional_header_table.update([tuple(pattern)] * count)
            frequent_items = {item for (item, count) in conditional_header_table.items() if count >= min_support}
            if frequent_items:
                conditional_transactions = []
                for (pattern, count) in conditional_pattern_base:
                    filtered_pattern = [i for i in pattern if i in frequent_items]
                    if filtered_pattern:
                        conditional_transactions.extend([filtered_pattern] * count)
                sorted_items = sorted(frequent_items, key=lambda x: (-conditional_header_table[x], x))
                sorted_item_map = {item: idx for (idx, item) in enumerate(sorted_items)}

                def sort_transaction(transaction: List[str]) -> List[str]:
                    return sorted(transaction, key=lambda x: (-conditional_header_table[x], sorted_item_map[x]))
                conditional_transactions = [sort_transaction(transaction) for transaction in conditional_transactions]
                if conditional_transactions:
                    (conditional_tree, conditional_header) = create_fp_tree(conditional_transactions, conditional_header_table)
                    if conditional_header:
                        mine_tree(conditional_header, conditional_tree, new_prefix, frequent_itemsets)
    frequent_itemsets = []
    mine_tree(header_table, fp_tree, [], frequent_itemsets)
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """
    supported_metrics = ['euclidean', 'manhattan', 'chebyshev', 'cosine']

    def validate_inputs(x, y):
        if name not in supported_metrics:
            return False
        if not (isinstance(x, (list, tuple, np.ndarray)) and isinstance(y, (list, tuple, np.ndarray))):
            return False
        try:
            x = np.array(x, dtype=float).ravel()
            y = np.array(y, dtype=float).ravel()
        except:
            return False
        if x.size == 0 or y.size == 0 or x.shape[0] != y.shape[0]:
            return False
        if name == 'cosine':
            norm_x = np.linalg.norm(x)
            norm_y = np.linalg.norm(y)
            if norm_x == 0 or norm_y == 0:
                return False
        return (x, y)

    def euclidean(x, y):
        return round(np.sqrt(np.sum((x - y) ** 2)), 4)

    def manhattan(x, y):
        return round(np.sum(np.abs(x - y)), 4)

    def chebyshev(x, y):
        return round(np.max(np.abs(x - y)), 4)

    def cosine(x, y):
        dot_product = np.dot(x, y)
        norm_x = np.linalg.norm(x)
        norm_y = np.linalg.norm(y)
        return round(1 - dot_product / (norm_x * norm_y), 4)

    def d(x, y):
        validation = validate_inputs(x, y)
        if validation is False:
            return -1
        (x, y) = validation
        if name == 'euclidean':
            return euclidean(x, y)
        elif name == 'manhattan':
            return manhattan(x, y)
        elif name == 'chebyshev':
            return chebyshev(x, y)
        elif name == 'cosine':
            return cosine(x, y)
        else:
            return -1
    return d

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """

    def hand_score(hand: list[int]) -> int:
        """Calculate the score of a hand in Blackjack."""
        aces = hand.count(1)
        total = sum(hand)
        while aces > 0 and total + 10 <= 21:
            total += 10
            aces -= 1
        return total
    player_score = hand_score(player)
    dealer_score = hand_score(dealer)
    if player_score > 21:
        return -1.0
    if dealer_score > 21:
        return 1.0
    if player_score > dealer_score:
        result = 1.0
    elif player_score < dealer_score:
        result = -1.0
    else:
        result = 0.0
    if result == 1.0:
        if len(player) == 2 and 1 in player and (10 in player):
            result = 1.5
    return result

import numpy as np
def average_pool2d(X: np.ndarray, pool_shape: tuple[int, int], stride: tuple[int, int], accum_grad: np.ndarray) -> tuple[list, list]:
    """Performs forward and backward passes of a 2-D average-pooling layer.

    The function must return a tuple (pooled_output, grad_input), both rounded
    to the nearest 4-th decimal and converted to regular Python lists using
    `tolist()`.
    """
    (N, C, H, W) = X.shape
    (p_h, p_w) = pool_shape
    (s_h, s_w) = stride
    out_h = (H - p_h) // s_h + 1
    out_w = (W - p_w) // s_w + 1
    pooled_output = np.zeros((N, C, out_h, out_w))
    for n in range(N):
        for c in range(C):
            for i in range(out_h):
                for j in range(out_w):
                    h_start = i * s_h
                    w_start = j * s_w
                    window = X[n, c, h_start:h_start + p_h, w_start:w_start + p_w]
                    pooled_output[n, c, i, j] = np.mean(window)
    grad_input = np.zeros_like(X)
    for n in range(N):
        for c in range(C):
            for i in range(out_h):
                for j in range(out_w):
                    h_start = i * s_h
                    w_start = j * s_w
                    grad = accum_grad[n, c, i, j] / (p_h * p_w)
                    grad_input[n, c, h_start:h_start + p_h, w_start:w_start + p_w] += np.full((p_h, p_w), grad)
    pooled_output_rounded = np.round(pooled_output, 4).tolist()
    grad_input_rounded = np.round(grad_input, 4).tolist()
    return (pooled_output_rounded, grad_input_rounded)

import numpy as np
from dataclasses import dataclass
@dataclass
class Leaf:
    """A terminal node that stores a prediction value."""
    value: object
@dataclass
class Node:
    """An internal decision-tree node.

    Attributes:
        feature (int):   Index of the feature to test.
        threshold (float): Threshold that splits the data.
        left (Node | Leaf):  Sub-tree for samples with feature value < threshold.
        right (Node | Leaf): Sub-tree for samples with feature value ≥ threshold.
    """
    feature: int
    threshold: float
    left: object
    right: object
def compare_trees(tree_a, tree_b):
    """Recursively checks whether *tree_a* and *tree_b* are equivalent.

    Args:
        tree_a: Root node of the first decision tree (Node or Leaf).
        tree_b: Root node of the second decision tree (Node or Leaf).

    Returns:
        True if the two trees are equivalent, False otherwise.
    """
    if isinstance(tree_a, Leaf) and isinstance(tree_b, Leaf):
        val_a = np.array(tree_a.value) if isinstance(tree_a.value, list) else tree_a.value
        val_b = np.array(tree_b.value) if isinstance(tree_b.value, list) else tree_b.value
        if isinstance(val_a, np.ndarray) and isinstance(val_b, np.ndarray):
            return np.allclose(val_a, val_b, atol=1e-08)
        elif isinstance(val_a, np.ndarray) or isinstance(val_b, np.ndarray):
            return False
        else:
            return np.isclose(val_a, val_b, atol=1e-08)
    if isinstance(tree_a, Node) and isinstance(tree_b, Node):
        if not (tree_a.feature == tree_b.feature and np.isclose(tree_a.threshold, tree_b.threshold, atol=1e-08)):
            return False
        return compare_trees(tree_a.left, tree_b.left) and compare_trees(tree_a.right, tree_b.right)
    return False

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool) -> Tuple[bool, bool]:
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def check_space(space: Space, is_tuple_space: bool) -> bool:
        if is_tuple_space:
            if isinstance(space, (Tuple, Dict)):
                return all((isinstance(sub, Box) for sub in space.spaces.values()))
            else:
                raise ValueError('The space is marked as composite but is not a Tuple or Dict.')
        else:
            return isinstance(space, Box)
    cont_action = check_space(env.action_space, tuple_action)
    cont_obs = check_space(env.observation_space, tuple_obs)
    return (cont_action, cont_obs)

import numpy as np
def root_mean_squared_log_error(actual: list[float], predicted: list[float]) -> float:
    """Calculate the Root Mean Squared Logarithmic Error (RMSLE) between two lists."""
    if len(actual) != len(predicted):
        return -1
    if any((x < 0 for x in actual)) or any((x < 0 for x in predicted)):
        return -1
    n = len(actual)
    sum_squared_log_diff = 0.0
    for (a, p) in zip(actual, predicted):
        log_diff = np.log1p(p) - np.log1p(a)
        sum_squared_log_diff += log_diff ** 2
    rmsle = np.sqrt(sum_squared_log_diff / n)
    return round(rmsle, 4)

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in {0, 1, 2}:
        return -1
    x = np.array(x, dtype=float)
    s = 1 / (1 + np.exp(-x))
    if order == 0:
        result = s
    elif order == 1:
        result = s * (1 - s)
    elif order == 2:
        result = s * (1 - s) * (1 - 2 * s)
    result = np.round(result, 4)
    if x.ndim == 0:
        return float(result)
    else:
        return result.tolist()

