    all_frequent = {frozenset(itemset): item_counts[frozenset(itemset)] for itemset in current_frequent}
    while current_frequent:
        frequent_itemsets.extend(sorted((tuple(sorted(itemset)) for itemset in current_frequent)))
        candidates = generate_candidates(current_frequent)
        frequent_k_minus_1_set = {frozenset(itemset) for itemset in current_frequent}
