import itertools
def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """
    freq_items = Counter()
    for transaction in transactions:
        freq_items.update(transaction)
    freq_items = {item: count for (item, count) in freq_items.items() if count >= min_sup}
    sorted_items = sorted(freq_items.keys())
    item_order = {item: idx for (idx, item) in enumerate(sorted_items)}
    filtered_transactions = []
    for transaction in transactions:
        ordered = sorted((item for item in transaction if item in freq_items), key=lambda x: item_order[x], reverse=True)
        if ordered:
            filtered_transactions.append(ordered)
    if not freq_items:
        return []

    class FPNode:

        def __init__(self, value=None, count=0, parent=None):
            self.value = value
            self.count = count
            self.parent = parent
            self.children = {}
            self.link = None

        def increment(self, count):
            self.count += count

    def update_header(node, headertable):
        while node.link is not None:
            node = node.link
        node.link = headertable[node.value]

    def update_tree(ordered_items, node, headertable, count):
        if not ordered_items:
            return
        item = ordered_items[0]
        if item in node.children:
            next_node = node.children[item]
            next_node.increment(count)
        else:
            next_node = FPNode(value=item, count=count, parent=node)
            node.children[item] = next_node
            if headertable[item] is None:
                headertable[item] = next_node
            else:
                update_header(next_node, headertable)
        update_tree(ordered_items[1:], next_node, headertable, count)
    root = FPNode()
    header_table = {item: None for item in freq_items}
    for trans in filtered_transactions:
        update_tree(trans, root, header_table, 1)

    def mine_tree(header_table, min_sup, prefix, freq_items):
        frequent_itemsets = []
        items = sorted(header_table.keys(), key=lambda x: freq_items[x], reverse=True)
        for item in items:
            new_prefix = tuple(sorted(list(prefix) + [item]))
            frequent_itemsets.append(new_prefix)
            cond_patterns = []
            node = header_table[item]
            while node is not None:
                path = []
                count = node.count
                current = node.parent
                while current.value is not None:
                    path.append((current.value, count))
                    current = current.parent
                if path:
                    cond_patterns.append((path, count))
                node = node.link
            cond_freq = Counter()
            for (path, count) in cond_patterns:
                for (val, _) in path:
                    cond_freq[val] += count
            sorted_cond_items = sorted(cond_freq.items(), key=lambda x: item_order[x[0]], reverse=True)
            if not sorted_cond_items:
                continue
            cond_items = [item for (item, count) in sorted_cond_items if count >= min_sup]
            if not cond_items:
                continue
            cond_trans = []
            for (path, count) in cond_patterns:
                filtered_path = [val for (val, _) in path if val in cond_items]
                if filtered_path:
                    cond_trans.append((filtered_path, count))
            if not cond_trans:
                continue
            item_order_cond = {item: idx for (idx, item) in enumerate(sorted(cond_items))}
            reordered_trans = []
            for (path, count) in cond_trans:
                reordered = sorted(path, key=lambda x: item_order_cond[x], reverse=True)
                reordered_trans.append((reordered, count))
            cond_root = FPNode()
            cond_header = {item: None for item in cond_items}
            for (cond_trans, count) in reordered_trans:
                update_tree(cond_trans, cond_root, cond_header, count)
            cond_freq_items = {item: cond_freq[item] for item in cond_items}
            cond_frequent = mine_tree(cond_header, min_sup, prefix + (item,), cond_freq_items)
            frequent_itemsets.extend(cond_frequent)
        return frequent_itemsets
    all_frequent = mine_tree(header_table, min_sup, tuple(), freq_items)
    all_frequent = sorted(set(all_frequent), key=lambda x: (len(x), x))
    return all_frequent

import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """

    def helper(x):
        if isinstance(x, (list, np.ndarray)):
            return type(x)([helper(sub) for sub in x])
        elif isinstance(x, float):
            return float(np.round(x * (1 - x), 4))
        else:
            return float(np.round(float(x) * (1 - float(x)), 4))
    if isinstance(z, (int, float)):
        return helper(z)
    elif isinstance(z, np.ndarray):
        return np.array([helper(row) for row in z])
    elif isinstance(z, list):
        return helper(z)
    else:
        raise TypeError('Unsupported input type: {}'.format(type(z)))

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if root.y is not None:
        return root.y
    if task_type == 'classification':
        feature_value = features[root.label]
        for (i, child) in enumerate(root.child):
            if child.x == feature_value:
                return predict_sample(child, features, task_type)
        return predict_sample(root.child[1], features, task_type)
    elif task_type == 'regression':
        feature_value = features[root.label]
        if feature_value <= root.s:
            return predict_sample(root.child[0], features, task_type)
        else:
            return predict_sample(root.child[1], features, task_type)

