import math
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> list[int]:
    """
    Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    np.random.seed(42)
    (n_samples, n_features) = data.shape
    phi = 2 * np.log(n_samples - 1) - 2 * (n_samples - 1) / n_samples

    def c(n):
        if n <= 1:
            return 0
        return 2 * np.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n

    class IsolationNode:

        def __init__(self, data_indices):
            self.left = None
            self.right = None
            self.split_feature = None
            self.split_threshold = None
            self.data_indices = data_indices
            self.height = 0

        def split(self, max_depth):
            if len(self.data_indices) <= 1 or self.height >= max_depth:
                return
            feature = np.random.randint(n_features)
            current_data = data[self.data_indices, feature]
            (min_val, max_val) = (np.min(current_data), np.max(current_data))
            threshold = np.random.uniform(min_val, max_val)
            left_indices = []
            right_indices = []
            for idx in self.data_indices:
                if data[idx, feature] < threshold:
                    left_indices.append(idx)
                else:
                    right_indices.append(idx)
            if not left_indices or not right_indices:
                return
            self.split_feature = feature
            self.split_threshold = threshold
            self.left = IsolationNode(left_indices)
            self.right = IsolationNode(right_indices)
            self.left.height = self.height + 1
            self.right.height = self.height + 1
            self.left.split(max_depth)
            self.right.split(max_depth)

    class IsolationTree:

        def __init__(self, data_indices, height_limit):
            self.root = IsolationNode(data_indices)
            self.root.split(height_limit)

        def path_length(self, x):
            path_len = 0
            node = self.root
            while node.left is not None and node.right is not None:
                feature = node.split_feature
                threshold = node.split_threshold
                if x[feature] < threshold:
                    node = node.left
                else:
                    node = node.right
                path_len += 1
            return path_len + c(len(node.data_indices))

    def height_limit(samples):
        return math.ceil(math.log2(samples))
    trees = []
    for _ in range(n_trees):
        if sample_size >= n_samples:
            indices = np.arange(n_samples)
        else:
            indices = np.random.choice(n_samples, size=sample_size, replace=False)
        trees.append(IsolationTree(indices, height_limit(sample_size)))
    avg_path_lengths = np.zeros(n_samples)
    for tree in trees:
        for i in range(n_samples):
            avg_path_lengths[i] += tree.path_length(data[i])
    avg_path_lengths /= n_trees
    scores = 2 ** (-(avg_path_lengths / phi))
    if epsilon <= 0:
        return []
    n_outliers = max(1, int(np.ceil(epsilon * n_samples)))
    outlier_indices = np.argsort(scores)[-n_outliers:]
    outlier_indices = outlier_indices[np.argsort(outlier_indices)]
    return outlier_indices.tolist()
