def fp_growth(transactions: List[List[str]], min_support: int) -> List[List[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth."""
    if not transactions:
        return []
    header_table = Counter()
    for transaction in transactions:
        header_table.update(transaction)
    filtered_header_table = {item: count for (item, count) in header_table.items() if count >= min_support}
    if not filtered_header_table:
        return []
    sorted_items = sorted(filtered_header_table.keys())
    processed_transactions = []
    for transaction in transactions:
