        filtered_transaction.sort()
        processed_transactions.append(filtered_transaction)
    root = FPTreeNode('Null', 1, None)
    build_fp_tree(root, processed_transactions, filtered_header_table)
    frequent_itemsets = []
    for item in sorted_items:
        if filtered_header_table[item] >= min_support:
            conditional_patterns = []
            current = filtered_header_table[item]
            while current:
                path = []
                node = current
                while node.parent:
                    path.append((node.parent.name, node.count))
                    node = node.parent
                path.reverse()
                conditional_patterns.append((path[1:], path[0][1]))
                current = current.node_link
            conditional_items = Counter()
            for (pattern, count) in conditional_patterns:
                for (item_name, _) in pattern:
                    conditional_items[item_name] += count
            conditional_items = {item: count for (item, count) in conditional_items.items() if count >= min_support}
            if conditional_items:
                conditional_transactions = []
                for (pattern, count) in conditional_patterns:
                    filtered_pattern = [(item, count) for (item, _) in pattern if item in conditional_items]
                    conditional_transactions.extend([tuple((item for (item, _) in filtered_pattern))] * count)
                conditional_frequent = fp_growth([list(t) for t in conditional_transactions], min_support)
                for itemset in conditional_frequent:
                    if item not in itemset:
                        frequent_itemsets.append(itemset + [item])
    for item in sorted_items:
        frequent_itemsets.append([item])
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets
