import itertools
def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """
    freq_items = Counter()
    for transaction in transactions:
        freq_items.update(transaction)
    freq_items = {item: count for (item, count) in freq_items.items() if count >= min_sup}
    item_order = sorted(freq_items.keys(), key=lambda x: freq_items[x], reverse=True)
    item_to_index = {item: idx for (idx, item) in enumerate(item_order)}

    class FPNode:

        def __init__(self, item, count, parent):
            self.item = item
            self.count = count
            self.parent = parent
            self.children = {}
            self.link = None
    root = FPNode(None, 0, None)
    header_table = defaultdict(lambda : None)
    for transaction in transactions:
        filtered = [item for item in transaction if item in freq_items]
        filtered.sort(key=lambda x: item_to_index[x], reverse=True)
        if filtered:
            _update_tree(filtered, root, header_table, item_to_index)

    def _update_tree(items, node, header_table, item_to_index):
        if not items:
            return
        item = items[0]
        if item in node.children:
            node.children[item].count += 1
        else:
            new_node = FPNode(item, 1, node)
            node.children[item] = new_node
            if header_table[item] is None:
                header_table[item] = new_node
            else:
                _update_link(header_table[item], new_node)
        _update_tree(items[1:], node.children[item], header_table, item_to_index)

    def _update_link(old_node, new_node):
        while old_node.link is not None:
            old_node = old_node.link
        old_node.link = new_node

    def _mine_tree(header_table, min_sup, prefix, frequent_items):
        ordered_items = sorted(header_table.keys(), key=lambda x: freq_items[x])
        for item in ordered_items:
            new_prefix = tuple(sorted(prefix + [item]))
            frequent_items.append(new_prefix)
            cond_patterns = []
            node = header_table[item]
            while node is not None:
                path = []
                count = node.count
                current = node.parent
                while current.item is not None:
                    path.append((current.item, count))
                    current = current.parent
                if path:
                    cond_patterns.append((path, count))
                node = node.link
            cond_items = Counter()
            for (path, count) in cond_patterns:
                for (item, _) in path:
                    cond_items[item] += count
            cond_items = {item: count for (item, count) in cond_items.items() if count >= min_sup}
            if cond_items:
                cond_order = sorted(cond_items.keys(), key=lambda x: item_to_index[x], reverse=True)
                cond_transactions = []
                for (path, count) in cond_patterns:
                    filtered = [item for (item, _) in path if item in cond_items]
                    filtered.sort(key=lambda x: item_to_index[x], reverse=True)
                    if filtered:
                        for _ in range(count):
                            cond_transactions.append(filtered)
                _mine_cond_tree(cond_transactions, min_sup, cond_items, item_to_index, prefix + [item], frequent_items)

    def _mine_cond_tree(transactions, min_sup, freq_items, item_to_index, prefix, frequent_items):
        if not transactions:
            return
        cond_freq = Counter()
        for transaction in transactions:
            cond_freq.update(transaction)
        cond_freq = {item: count for (item, count) in cond_freq.items() if count >= min_sup}
        if not cond_freq:
            return
        cond_order = sorted(cond_freq.keys(), key=lambda x: item_to_index[x], reverse=True)
        cond_item_to_index = {item: idx for (idx, item) in enumerate(cond_order)}
        cond_root = FPNode(None, 0, None)
        cond_header_table = defaultdict(lambda : None)
        for transaction in transactions:
            filtered = [item for item in transaction if item in cond_freq]
            filtered.sort(key=lambda x: cond_item_to_index[x], reverse=True)
            if filtered:
                _update_tree(filtered, cond_root, cond_header_table, cond_item_to_index)
        _mine_tree(cond_header_table, min_sup, prefix, frequent_items)
    frequent_itemsets = []
    _mine_tree(header_table, min_sup, [], frequent_itemsets)
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets
