def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    """Train a MaxEnt classifier using Generalized Iterative Scaling (GIS)
    and predict labels for test data."""
    feature_index = {}
    features = []
    for i in range(X_train.shape[0]):
        x = X_train[i]
        y = y_train[i]
        for j in range(x.shape[0]):
            val = x[j]
            if val is not None or val != '':
                key = (j, val, y)
                if key not in feature_index:
                    feature_index[key] = len(feature_index)
                    features.append(key)
    num_features = len(feature_index)
    num_labels = len(set(y_train))
    labels = sorted(set(y_train))
    weights = np.zeros(num_features)
    empirical_expectations = defaultdict(float)
    total_samples = len(y_train)
    for i in range(total_samples):
        x = X_train[i]
        y = y_train[i]
        for j in range(x.shape[0]):
            val = x[j]
            if val is not None or val != '':
                key = (j, val, y)
                empirical_expectations[feature_index[key]] += 1 / total_samples
    for iteration in range(n_iter):
        model_expectations = defaultdict(float)
        partition_function = 0.0
        for i in range(total_samples):
            x = X_train[i]
            y = y_train[i]
            score = 0.0
            feature_map = defaultdict(int)
            for j in range(x.shape[0]):
                val = x[j]
                if val is not None or val != '':
                    key = (j, val, y)
                    if key in feature_index:
                        feature_map[feature_index[key]] += 1
            for (fid, count) in feature_map.items():
                score += weights[fid] * count
            label_scores = {}
            for label in labels:
                feature_map_label = defaultdict(int)
                for j in range(x.shape[0]):
                    val = x[j]
                    if val is not None or val != '':
                        key = (j, val, label)
                        if key in feature_index:
                            feature_map_label[feature_index[key]] += 1
                label_score = 0.0
                for (fid, count) in feature_map_label.items():
                    label_score += weights[fid] * count
                label_scores[label] = label_score
            max_score = max(label_scores.values())
            for label in label_scores:
                label_scores[label] -= max_score
            Z = 0.0
            for label in label_scores:
                Z += math.exp(label_scores[label])
            for label in label_scores:
                prob = math.exp(label_scores[label]) / Z
                for j in range(x.shape[0]):
                    val = x[j]
                    if val is not None or val != '':
                        key = (j, val, label)
                        if key in feature_index:
                            fid = feature_index[key]
                            model_expectations[fid] += prob / total_samples
        max_update = 0.0
        for fid in range(num_features):
            if empirical_expectations[fid] != model_expectations[fid]:
                update = math.log(empirical_expectations[fid] / model_expectations[fid])
                weights[fid] += update
                max_update = max(max_update, abs(update))
        if max_update < epsilon:
            break
    predictions = []
    for i in range(X_test.shape[0]):
        x = X_test[i]
        best_label = None
        best_score = -float('inf')
        for label in labels:
