def adaboost_1d_predict(x_train: list[float], y_train: list[int], x_test: list[float], epsilon: float=0.0) -> list[int]:
    """Trains a 1-D AdaBoost ensemble and returns predictions.

    Parameters
    ----------
    x_train : list[float]
        Training sample values (one-dimensional).
    y_train : list[int]
        Labels corresponding to *x_train* (each value must be 1 or –1).
    x_test : list[float]
        Sample values to classify after training.
    epsilon : float, default 0.0
        Upper bound on the allowed training error.  Training stops once
        the ensemble’s training error ≤ *epsilon*.

    Returns
    -------
    list[int]
        Predicted labels (1 or –1) for every value in *x_test*.
    """
    n = len(x_train)
    D = [1.0 / n] * n
    ensemble = []
    train_data = list(zip(x_train, y_train))
    while True:
        pred_train = [1.0 for _ in range(n)]
        for (alpha, d, theta) in ensemble:
            for i in range(n):
                x = train_data[i][0]
                if d == 0:
                    if x <= theta:
                        pred_train[i] += alpha
                    else:
                        pred_train[i] -= alpha
                elif x > theta:
                    pred_train[i] += alpha
                else:
                    pred_train[i] -= alpha
        training_error = sum((sign(p) != y for (p, y) in zip(pred_train, y_train))) / n
        if epsilon == 0.0 or training_error <= epsilon:
            break
        sorted_data = sorted(train_data, key=lambda x: x[0])
        x_sorted = [x for (x, _) in sorted_data]
        y_sorted = [y for (_, y) in sorted_data]
        thresholds = []
        for i in range(n - 1):
            thresholds.append((x_sorted[i] + x_sorted[i + 1]) / 2)
        best_error = float('inf')
        best_stump = None
        for theta in thresholds:
            for d in [0, 1]:
                error = 0.0
                for i in range(n):
                    x = x_sorted[i]
                    y = y_sorted[i]
                    if d == 0:
                        if x <= theta:
                            pred = 1
                        else:
                            pred = -1
                    elif x > theta:
                        pred = 1
                    else:
                        pred = -1
                    if pred != y:
                        error += D[i]
                if error < best_error:
                    best_error = error
                    best_stump = (d, theta)
        err = best_error
        alpha = 0.5 * math.log((1 - err) / err)
        ensemble.append((alpha, best_stump[0], best_stump[1]))
        Z = 0.0
        for i in range(n):
            x = x_sorted[i]
            y = y_sorted[i]
            (d, theta) = best_stump
            if d == 0:
                if x <= theta:
                    pred = 1
                else:
                    pred = -1
            elif x > theta:
                pred = 1
            else:
                pred = -1
            D[i] *= math.exp(-alpha * y * pred)
            Z += D[i]
        for i in range(n):
            D[i] /= Z
    predictions = []
    for x in x_test:
        score = 0.0
        for (alpha, d, theta) in ensemble:
            if d == 0:
                if x <= theta:
                    score += alpha
                else:
                    score -= alpha
            elif x > theta:
                score += alpha
            else:
                score -= alpha
        predictions.append(sign(score))
    return predictions
