import math
def escape_fire_maze(grid: list[str]) -> int:
    """Escape from a maze with spreading fire.

    Parameters
    ----------
    grid : list[str]
        Square maze represented as a list of strings. Each character must be
        '.', '#', or 'F'.  The agent starts at the upper-left corner (0,0) and
        wishes to reach the lower-right corner (n-1,n-1).  Fire starts in every
        cell marked with 'F' and spreads to the four neighbouring cells every
        minute.  The fire spreads first, then the agent moves.

    Returns
    -------
    int
        Minimum number of minutes required for the agent to reach the goal
        without entering a burning cell, or -1 if this is impossible.
    """
    n = len(grid)
    if n == 1:
        return 0 if grid[0][0] == '.' else -1
    directions = [(-1, 0), (1, 0), (0, -1), (0, 1)]
    fire_time = [[math.inf for _ in range(n)] for _ in range(n)]
    for i in range(n):
        for j in range(n):
            if grid[i][j] == 'F':
                fire_time[i][j] = 0
    fire_queue = deque()
    for i in range(n):
        for j in range(n):
            if grid[i][j] == 'F':
                fire_queue.append((i, j))
    fire_time_steps = 0
    while fire_queue:
        for _ in range(len(fire_queue)):
            (i, j) = fire_queue.popleft()
            for (di, dj) in directions:
                (ni, nj) = (i + di, j + dj)
                if 0 <= ni < n and 0 <= nj < n and (grid[ni][nj] == '.') and (fire_time[ni][nj] == math.inf):
                    fire_time[ni][nj] = fire_time[i][j] + 1
                    fire_queue.append((ni, nj))
        fire_time_steps += 1
    if grid[0][0] == 'F' or grid[n - 1][n - 1] == 'F':
        return -1
    if grid[0][0] == '#' or grid[n - 1][n - 1] == '#':
        return -1
    agent_queue = deque()
    agent_queue.append((0, 0, 0))
    visited = [[False for _ in range(n)] for _ in range(n)]
    visited[0][0] = True
    while agent_queue:
        (i, j, time) = agent_queue.popleft()
        if i == n - 1 and j == n - 1:
            return time
        for (di, dj) in directions:
            (ni, nj) = (i + di, j + dj)
            if 0 <= ni < n and 0 <= nj < n and (not visited[ni][nj]) and (grid[ni][nj] != '#') and (fire_time[ni][nj] > time + 1):
                visited[ni][nj] = True
                agent_queue.append((ni, nj, time + 1))
    return -1

import numpy as np
def knn_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, k: int, metric: str='euclidean') -> np.ndarray:
    """
    Predicts the class labels for test samples using the k-NN classifier.

    Parameters:
    - X_train: np.ndarray, shape (n_samples, n_features), training data
    - y_train: np.ndarray, shape (n_samples,), training labels
    - X_test: np.ndarray, shape (m_samples, n_features), test data
    - k: int, number of neighbors to consider
    - metric: str, distance metric to use ('euclidean', 'manhattan', 'cosine')

    Returns:
    - np.ndarray, shape (m_samples,), predicted labels for test samples
    """
    (m, n) = X_test.shape
    n_train = X_train.shape[0]
    y_pred = np.zeros(m, dtype=y_train.dtype)
    if k <= 0 or k > n_train:
        raise ValueError(f'k must be between 1 and {n_train}')
    if metric == 'euclidean':

        def distance(x1, x2):
            return np.sqrt(np.sum((x1 - x2) ** 2, axis=1))
    elif metric == 'manhattan':

        def distance(x1, x2):
            return np.sum(np.abs(x1 - x2), axis=1)
    elif metric == 'cosine':

        def distance(x1, x2):
            dot = np.dot(x1, x2.T)
            norm1 = np.linalg.norm(x1, axis=1, keepdims=True)
            norm2 = np.linalg.norm(x2, axis=1)
            return 1 - dot / (norm1 * norm2 + 1e-10)
    else:

        def distance(x1, x2):
            return np.sqrt(np.sum((x1 - x2) ** 2, axis=1))
    for i in range(m):
        x_test = X_test[i]
        dists = distance(X_train, x_test)
        nearest_indices = np.argsort(dists)[:k]
        nearest_labels = y_train[nearest_indices]
        (unique_labels, counts) = np.unique(nearest_labels, return_counts=True)
        winner = unique_labels[np.argmin(unique_labels[counts == np.max(counts)])]
        y_pred[i] = winner
    return y_pred
