def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: int | None=None) -> list[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test.

    Parameters
    ----------
    X_train : np.ndarray
        Training feature matrix of shape (n_samples, n_features).
    y_train : np.ndarray
        Integer class labels for the training data, shape (n_samples,).
    X_test : np.ndarray
        Feature matrix to classify, shape (m_samples, n_features).
    max_depth : int | None, optional
        Maximum allowed depth of the tree. If None the depth is unlimited.

    Returns
    -------
    list[int]
        Predicted class label for each row in X_test.
    """

    class DecisionNode:

        def __init__(self, feature_idx=None, threshold=None, left=None, right=None, label=None, depth=0):
            self.feature_idx = feature_idx
            self.threshold = threshold
            self.left = left
            self.right = right
            self.label = label
            self.depth = depth

    def gini_impurity(y):
        (_, counts) = np.unique(y, return_counts=True)
        probabilities = counts / counts.sum()
        return 1 - np.sum(probabilities ** 2)

    def get_majority_class(y):
        (values, counts) = np.unique(y, return_counts=True)
        return values[np.argmax(counts)]

    def find_best_split(X, y):
        (n_samples, n_features) = X.shape
        best_gain = -1
        best_idx = None
        best_threshold = None
        for feature_idx in range(n_features):
            sorted_indices = np.argsort(X[:, feature_idx])
            X_sorted = X[sorted_indices, feature_idx]
            y_sorted = y[sorted_indices]
            unique_X = np.unique(X_sorted)
            thresholds = []
            for i in range(1, len(unique_X)):
                if unique_X[i] != unique_X[i - 1]:
                    thresholds.append((unique_X[i - 1] + unique_X[i]) / 2)
            for threshold in thresholds:
                left_mask = X_sorted <= threshold
                right_mask = X_sorted > threshold
                y_left = y_sorted[left_mask]
                y_right = y_sorted[right_mask]
                if len(y_left) == 0 or len(y_right) == 0:
                    continue
                gini_parent = gini_impurity(y_sorted)
                gini_left = gini_impurity(y_left)
                gini_right = gini_impurity(y_right)
                gain = gini_parent - len(y_left) / n_samples * gini_left - len(y_right) / n_samples * gini_right
                if gain > best_gain:
                    best_gain = gain
                    best_idx = feature_idx
                    best_threshold = threshold
        return (best_idx, best_threshold, best_gain)

    def build_tree(X, y, current_depth=0):
        if np.all(y == y[0]):
            return DecisionNode(label=int(y[0]), depth=current_depth)
        if max_depth is not None and current_depth >= max_depth:
            return DecisionNode(label=int(get_majority_class(y)), depth=current_depth)
        (best_idx, best_threshold, best_gain) = find_best_split(X, y)
        if best_gain <= 0:
            return DecisionNode(label=int(get_majority_class(y)), depth=current_depth)
        left_mask = X[:, best_idx] <= best_threshold
        right_mask = X[:, best_idx] > best_threshold
        left_node = build_tree(X[left_mask], y[left_mask], current_depth + 1)
        right_node = build_tree(X[right_mask], y[right_mask], current_depth + 1)
        return DecisionNode(feature_idx=best_idx, threshold=best_threshold, left=left_node, right=right_node, depth=current_depth)

    def predict_sample(sample, node):
        if node.label is not None:
            return node.label
        if sample[node.feature_idx] <= node.threshold:
            return predict_sample(sample, node.left)
        else:
            return predict_sample(sample, node.right)
    tree = build_tree(X_train, y_train)
    predictions = [predict_sample(X_test[i], tree) for i in range(X_test.shape[0])]
    return predictions
