    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def check_space(space: Space, is_composite: bool) -> bool:
        if is_composite:
            if isinstance(space, (Tuple, Dict)):
                return all((isinstance(subspace, Box) for subspace in space.spaces.values()))
            else:
                return False
        else:
            return isinstance(space, Box)
    cont_action = check_space(env.action_space, tuple_action)
    cont_obs = check_space(env.observation_space, tuple_obs)
    return (cont_action, cont_obs)

import numpy as np
def root_mean_squared_log_error(actual: list[float], predicted: list[float]) -> float:
    """Calculate the Root Mean Squared Logarithmic Error (RMSLE)."""
    if len(actual) != len(predicted):
        return -1
    if any((x < 0 for x in actual)) or any((x < 0 for x in predicted)):
        return -1
    n = len(actual)
    log_errors = [np.log(1 + predicted[i]) - np.log(1 + actual[i]) for i in range(n)]
    squared_errors = [e ** 2 for e in log_errors]
    mean_squared_error = sum(squared_errors) / n
    rmsle = np.sqrt(mean_squared_error)
    return round(rmsle, 4)

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in {0, 1, 2}:
        return -1
    x_array = np.array(x, dtype=float)
    sigma = 1 / (1 + np.exp(-x_array))
    if order == 1:
        result = sigma * (1 - sigma)
    elif order == 2:
        result = sigma * (1 - sigma) * (1 - 2 * sigma)
    else:
        result = sigma
    result = np.round(result, 4)
    if np.isscalar(x):
        return float(result)
    else:
        return result.tolist()

import itertools
from collections import defaultdict
from typing import List, Tuple, Hashable
def apriori_frequent_itemsets(transactions: List[List[Hashable]], min_sup: float) -> List[Tuple]:
    """Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list[Hashable]]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
    """
    num_transactions = len(transactions)
    if num_transactions == 0:
        return []
    min_support_count = min_sup * num_transactions
    item_counts = defaultdict(int)
    for transaction in transactions:
        for item in set(transaction):
            item_counts[item] += 1
    frequent_itemsets = []
    C1 = {item for (item, count) in item_counts.items() if count >= min_support_count}
    frequent_itemsets.extend(sorted((tuple([item]) for item in C1)))
    L = [set([frozenset(iset) for iset in frequent_itemsets])]
    k = 2
    while L[k - 1 - 1]:
        Ck = set()
        for itemset1 in L[k - 1 - 1]:
            for itemset2 in L[k - 1 - 1]:
                if len(itemset1.symmetric_difference(itemset2)) == 2:
                    union = itemset1.union(itemset2)
                    if len(union) == k:
                        Ck.add(union)
        Ck_pruned = set()
        for candidate in Ck:
            is_all_subsets_frequent = True
            for subset in itertools.combinations(candidate, k - 1):
                if frozenset(subset) not in L[k - 1 - 1]:
                    is_all_subsets_frequent = False
                    break
            if is_all_subsets_frequent:
                Ck_pruned.add(candidate)
        Lk = set()
        for candidate in Ck_pruned:
            count = 0
            for transaction in transactions:
                if candidate.issubset(transaction):
                    count += 1
            if count >= min_support_count:
                Lk.add(candidate)
        if Lk:
            frequent_itemsets.extend(sorted((tuple(sorted(iset)) for iset in Lk)))
            L.append(Lk)
        else:
            break
        k += 1
    return frequent_itemsets
