import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """

    def process(x):
        if isinstance(x, (float, int)):
            return round(float(x * (1 - x)), 4)
        elif isinstance(x, np.ndarray):
            return np.round(x * (1 - x), 4)
        elif isinstance(x, list):
            if not x:
                return []
            if all((isinstance(i, (float, int)) for i in x)):
                return [round(float(i * (1 - i)), 4) for i in x]
            else:
                return [process(row) for row in x]
        else:
            raise TypeError('Input must be a float, list, or NumPy array')
    return process(z)

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if root.y is not None:
        return root.y
    if task_type == 'classification':
        feature_value = features[root.label]
        for (i, child) in enumerate(root.child):
            if child.x == feature_value:
                return predict_sample(child, features, task_type)
        return predict_sample(root.child[1], features, task_type)
    elif task_type == 'regression':
        feature_value = features[root.label]
        if feature_value <= root.s:
            return predict_sample(root.child[0], features, task_type)
        else:
            return predict_sample(root.child[1], features, task_type)

import numpy as np
import math
from typing import List
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> List[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    np.random.seed(42)
    (n_samples, n_features) = data.shape
    if epsilon == 0:
        return []

    def c(n):
        if n <= 1:
            return 0
        return 2 * math.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n

    def height_limit(n):
        return math.ceil(math.log2(n))

    def build_tree(subsample_indices):
        subsample = subsample_indices
        tree = {}

        def build_node(indices, depth, path):
            n = len(indices)
            if n <= 1 or depth >= height_limit(n):
                tree[tuple(path)] = indices
                return {i: len(path) + c(n) for i in indices}
            else:
                feature = np.random.choice(n_features)
                min_val = data[indices, feature].min()
                max_val = data[indices, feature].max()
                threshold = np.random.uniform(min_val, max_val)
                left_indices = [i for i in indices if data[i, feature] < threshold]
                right_indices = [i for i in indices if data[i, feature] >= threshold]
                path_left = path + [(feature, threshold, 'left')]
                path_right = path + [(feature, threshold, 'right')]
                left_paths = build_node(left_indices, depth + 1, path_left) if left_indices else {}
                right_paths = build_node(right_indices, depth + 1, path_right) if right_indices else {}
                return {**left_paths, **right_paths}
        return build_node(subsample, 0, [])
    path_lengths = np.zeros(n_samples)
    for _ in range(n_trees):
        if sample_size >= n_samples:
            subsample_indices = np.arange(n_samples)
        else:
            subsample_indices = np.random.choice(n_samples, size=sample_size, replace=False)
        tree_path_lengths = build_tree(subsample_indices)
        for (idx, length) in tree_path_lengths.items():
            path_lengths[idx] += length
    path_lengths /= n_trees
    phi = 2 * math.log(n_samples - 1) - 2 * (n_samples - 1) / n_samples
    scores = [2 ** (-length / phi) for length in path_lengths]
    sorted_indices = np.argsort(scores)[::-1]
    n_outliers = max(0, int(n_samples * epsilon))
    outlier_indices = sorted_indices[:n_outliers]
    return outlier_indices.tolist()
