            feature_map = defaultdict(int)
            for j in range(x.shape[0]):
                val = x[j]
                if val is not None or val != '':
                    key = (j, val, label)
                    if key in feature_index:
                        fid = feature_index[key]
                        score += weights[fid]
            if score > best_score:
                best_score = score
                best_label = label
        predictions.append(best_label)
    return predictions
