import itertools
import itertools
from collections import defaultdict
def apriori_frequent_itemsets(transactions: list[list], min_sup: float) -> list[tuple]:
    """Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list[Hashable]]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
    """
    if not 0 < min_sup <= 1:
        raise ValueError('min_sup must be in the range (0, 1]')
    num_transactions = len(transactions)
    min_sup_count = int(min_sup * num_transactions)
    item_counts = defaultdict(int)
    for tx in transactions:
        for item in set(tx):
            item_counts[frozenset([item])] += 1
    frequent_itemsets = []
    for (itemset, count) in sorted(item_counts.items()):
        if count >= min_sup_count:
            frequent_itemsets.append(tuple(sorted(itemset)))
    result_by_length = {1: frequent_itemsets}
    result_flat = []
    k = 2
    while True:
        if k - 1 not in result_by_length or not result_by_length[k - 1]:
            break
        candidates = set()
        for itemset1 in result_by_length[k - 1]:
            for itemset2 in result_by_length[k - 1]:
                if itemset1[:-1] == itemset2[:-1] and itemset1[-1] < itemset2[-1]:
                    candidates.add(tuple(sorted(set(itemset1) | set(itemset2))))
        pruned_candidates = []
        for candidate in candidates:
            is_valid = True
            for subset in itertools.combinations(candidate, k - 1):
                subset_set = frozenset(subset)
                if not any((sorted(subset_set) == sorted(s) for s in result_by_length[k - 1])):
                    is_valid = False
                    break
            if is_valid:
                pruned_candidates.append(tuple(sorted(candidate)))
        candidate_support = defaultdict(int)
        for tx in transactions:
            tx_set = set(tx)
            for candidate in pruned_candidates:
                if set(candidate).issubset(tx_set):
                    candidate_support[candidate] += 1
        frequent_k = []
        for (candidate, count) in sorted(candidate_support.items()):
            if count >= min_sup_count:
                frequent_k.append(candidate)
        if not frequent_k:
            break
        result_by_length[k] = frequent_k
        k += 1
    result_flat = []
    for length in sorted(result_by_length):
        result_flat.extend(sorted(result_by_length[length]))
    return result_flat
