import numpy as np
import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """

    def compute_grad(x):
        return np.round(x * (1 - x), 4)
    is_array = isinstance(z, np.ndarray)
    is_list = isinstance(z, (list, tuple))
    if not is_array and (not is_list):
        return float(np.round(z * (1 - z), 4))
    arr = np.array(z, dtype=float)
    result = compute_grad(arr)

    def to_list(arr):
        if arr.ndim == 0:
            return float(arr)
        elif arr.ndim == 1:
            return [float(np.round(x, 4)) for x in arr]
        elif arr.ndim == 2:
            return [[float(np.round(x, 4)) for x in row] for row in arr]
        else:
            raise ValueError('Input rank is higher than 2')
    if is_array:
        return to_list(result)
    elif is_list:

        def nested_list_recurse(lst):
            if isinstance(lst, (list, tuple)):
                if len(lst) == 0:
                    return []
                return [nested_list_recurse(x) for x in lst]
            else:
                return float(np.round(lst * (1 - lst), 4))
        return nested_list_recurse(z)
