    feature_id = 0
    for (x, y) in zip(X_train, y_train):
        for (i, val) in enumerate(x):
            for label in label_set:
                key = (i, val, label)
                if key not in feature_index:
                    feature_index[key] = feature_id
                    feature_id += 1
    n_features = len(feature_index)
    n_labels = len(label_set)
    label_to_index = {label: idx for (idx, label) in enumerate(label_set)}
    index_to_label = {idx: label for (label, idx) in label_to_index.items()}
    E = defaultdict(float)
    for (x, y) in zip(X_train, y_train):
        features = extract_features(x)
        for (i, val) in features:
            E[i, val, y] += 1
    n_samples = len(X_train)
    for key in E:
        E[key] /= n_samples
    w = np.zeros(n_features)
    for iteration in range(n_iter):
        Q = defaultdict(float)
        Z_cache = {}
        for x in X_train:
            key = tuple(x)
            if key not in Z_cache:
                Z = 0.0
                for label in label_set:
                    score = 0.0
                    for (i, val) in enumerate(x):
                        key_fid = (i, val, label)
                        if key_fid in feature_index:
                            fid = feature_index[key_fid]
                            score += w[fid]
                    Z += math.exp(score)
                Z_cache[key] = Z
            Z = Z_cache[key]
            for label in label_set:
                score = 0.0
                for (i, val) in enumerate(x):
                    key_fid = (i, val, label)
                    if key_fid in feature_index:
                        fid = feature_index[key_fid]
                        score += w[fid]
                p = math.exp(score) / Z
                for (i, val) in enumerate(x):
                    key_fid = (i, val, label)
                    if key_fid in feature_index:
                        fid = feature_index[key_fid]
                        Q[i, val, label] += p
        for key in Q:
            Q[key] /= n_samples
        delta = 0.0
        for ((i, val, label), E_count) in E.items():
            Q_count = Q.get((i, val, label), 0.0)
            diff = E_count - Q_count
            fid = feature_index[i, val, label]
            w[fid] += math.log((E_count + 1e-10) / (Q_count + 1e-10))
            delta = max(delta, abs(w[fid]))
        if delta < epsilon:
            break
    predictions = []
    for x in X_test:
        best_label = None
        max_prob = -math.inf
        for label in label_set:
            score = 0.0
            for (i, val) in enumerate(x):
                key_fid = (i, val, label)
                if key_fid in feature_index:
                    fid = feature_index[key_fid]
                    score += w[fid]
            if score > max_prob:
                max_prob = score
                best_label = label
        predictions.append(best_label)
    return predictions
