import numpy as np
def glm_links():
    """Construct and return standard GLM link functions.

    Returns
    -------
    dict
        A three-entry dictionary (identity, log, logit) where each entry is a
        dictionary containing callables for the link, its inverse and its
        derivative with respect to μ. All functions must work with numpy
        scalars as well as 1-D/2-D numpy arrays via element-wise operations.
    """
    links = {}

    def identity_link(mu):
        return mu

    def identity_inv_link(eta):
        return eta

    def identity_derivative(mu):
        return np.ones_like(mu)
    links['identity'] = {'link': identity_link, 'inv_link': identity_inv_link, 'deriv': identity_derivative}

    def log_link(mu):
        return np.log(mu)

    def log_inv_link(eta):
        return np.exp(eta)

    def log_derivative(mu):
        return 1.0 / mu
    links['log'] = {'link': log_link, 'inv_link': log_inv_link, 'deriv': log_derivative}

    def logit_link(mu):
        return np.log(mu / (1.0 - mu))

    def logit_inv_link(eta):
        return 1.0 / (1.0 + np.exp(-eta))

    def logit_derivative(mu):
        return 1.0 / (mu * (1.0 - mu))
    links['logit'] = {'link': logit_link, 'inv_link': logit_inv_link, 'deriv': logit_derivative}
    return links

import numpy as np
def softplus(x, deriv: bool=False):
    """Compute the SoftPlus activation function or its derivative (sigmoid).

    Args:
        x: A scalar, list, or NumPy array of real values.
        deriv: If True, computes the derivative (sigmoid). If False (default), computes SoftPlus.

    Returns:
        A Python list of results (or a float if input is a scalar), rounded to 4 decimal places.
    """
    x = np.asarray(x, dtype=np.float64)
    if deriv:
        result = 1.0 / (1.0 + np.exp(-x))
    else:
        result = np.where(x < -30, np.exp(x), np.log1p(np.exp(-x)) + x)
    result_rounded = np.round(result, decimals=4)
    if np.isscalar(x):
        return float(result_rounded)
    else:
        return result_rounded.tolist()

from collections import Counter, defaultdict
def fp_growth(transactions: list[list[str]], min_support: int) -> list[list[str]]:
    """Discover every frequent item-set in *transactions* with FP-Growth."""
    if not transactions:
        return []
    item_counts = Counter()
    for transaction in transactions:
        item_counts.update(set(transaction))
    frequent_items = {item for (item, count) in item_counts.items() if count >= min_support}
    if not frequent_items:
        return []
    sorted_items = sorted(frequent_items, key=lambda x: (-item_counts[x], x))
    item_map = {item: idx for (idx, item) in enumerate(sorted_items)}
    item_support = {item: count for (item, count) in item_counts.items() if item in frequent_items}

    class FPNode:

        def __init__(self, name, count, parent):
            self.name = name
            self.count = count
            self.parent = parent
            self.children = {}
            self.node_links = None

        def increment(self, count):
            self.count += count

    def update_header(node_to_link, target_node):
        while node_to_link.node_links and node_to_link.node_links[1]:
            node_to_link = node_to_link.node_links[1]
        node_to_link.node_links = (target_node, node_to_link.node_links[0] if node_to_link.node_links else None)

    def update_tree(items, node, header_table, count):
        if not items:
            return
        item = items[0]
        remaining_items = items[1:]
        if item in node.children:
            next_node = node.children[item]
            next_node.count += count
        else:
            next_node = FPNode(item, count, node)
            node.children[item] = next_node
            if header_table[item][0] is None:
                header_table[item] = (next_node, header_table[item][1])
            else:
                update_header(header_table[item][0], next_node)
        update_tree(remaining_items, next_node, header_table, count)
    header_table = {item: (None, item_support[item]) for item in sorted_items}
    root = FPNode(None, 1, None)
    for transaction in transactions:
        filtered_transaction = [item for item in transaction if item in frequent_items]
        filtered_transaction.sort(key=lambda x: -item_map[x])
        if filtered_transaction:
            update_tree(filtered_transaction, root, header_table, 1)

    def mine_tree(header_table, min_support, prefix, frequent_itemsets):
        ordered_items = [item for (item, _) in sorted(header_table.items(), key=lambda x: x[1][1])]
        for item in ordered_items:
            new_prefix = sorted(prefix + [item])
            frequent_itemsets.append(new_prefix)
            conditional_pattern_base = []
            beta = header_table[item][0]
            while beta:
                path = []
                alpha = beta.parent
                while alpha.name is not None:
                    path.append((alpha.name, alpha.count))
                    alpha = alpha.parent
                if path:
                    conditional_path = [(name, count * beta.count) for (name, count) in path]
                    conditional_pattern_base.append(conditional_path)
                beta = beta.node_links[1] if beta.node_links else None
            conditional_counts = Counter()
            for (path, count) in conditional_pattern_base:
                conditional_counts.update([path[0][0]] * count)
            conditional_frequent_items = {name for (name, count) in conditional_counts.items() if count >= min_support}
            if not conditional_frequent_items:
                continue
            conditional_transactions = []
            for (path, count) in conditional_pattern_base:
                filtered_path = [name for (name, _) in path if name in conditional_frequent_items]
                if filtered_path:
                    conditional_transactions.extend([filtered_path] * count)
            conditional_itemsets = fp_growth(conditional_transactions, min_support)
            for itemset in conditional_itemsets:
                if len(itemset) > 1:
                    frequent_itemsets.append(sorted(prefix + [item] + itemset))
    frequent_itemsets = []
    if header_table:
        mine_tree(header_table, min_support, [], frequent_itemsets)
    unique_frequent_itemsets = []
    for itemset in frequent_itemsets:
        if itemset not in unique_frequent_itemsets:
            unique_frequent_itemsets.append(itemset)
    unique_frequent_itemsets.sort(key=lambda x: (len(x), x))
    final_result = []
    for itemset in unique_frequent_itemsets:
        if len(itemset) == 1 and item_support[itemset[0]] < min_support:
            continue
        final_result.append(itemset)
    return final_result

import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """
    supported_metrics = ['euclidean', 'manhattan', 'chebyshev', 'cosine']

    def validate_inputs(x, y):
        try:
            x = np.array(x, dtype=float)
            y = np.array(y, dtype=float)
        except (ValueError, TypeError):
            return (False, None, None)
        if x.ndim != 1 or y.ndim != 1:
            return (False, None, None)
        if x.size == 0 or y.size == 0 or x.size != y.size:
            return (False, None, None)
        return (True, x, y)

    def euclidean(x, y):
        (valid, a, b) = validate_inputs(x, y)
        if not valid:
            return -1
        dist = np.sqrt(np.sum((a - b) ** 2))
        return round(dist, 4)

    def manhattan(x, y):
        (valid, a, b) = validate_inputs(x, y)
        if not valid:
            return -1
        dist = np.sum(np.abs(a - b))
        return round(dist, 4)

    def chebyshev(x, y):
        (valid, a, b) = validate_inputs(x, y)
        if not valid:
            return -1
        dist = np.max(np.abs(a - b))
        return round(dist, 4)

    def cosine(x, y):
        (valid, a, b) = validate_inputs(x, y)
        if not valid:
            return -1
        dot_product = np.dot(a, b)
        norm_x = np.linalg.norm(a)
        norm_y = np.linalg.norm(b)
        if norm_x == 0 or norm_y == 0:
            return -1
        similarity = dot_product / (norm_x * norm_y)
        dist = 1 - similarity
        return round(dist, 4)

    def d(x, y):
        if name not in supported_metrics:
            return -1
        if name == 'euclidean':
            return euclidean(x, y)
        elif name == 'manhattan':
            return manhattan(x, y)
        elif name == 'chebyshev':
            return chebyshev(x, y)
        elif name == 'cosine':
            return cosine(x, y)
    return d

def blackjack_outcome(player: list[int], dealer: list[int]) -> float:
    """Evaluate the outcome of a finished round of Blackjack.

    The function **must** follow the rules presented in the task description.

    Args:
        player: List of integers (1–10) representing the player's final hand. 1 is Ace.
        dealer: List of integers (1–10) representing the dealer's final hand.

    Returns:
        The player's reward as a float. Possible values are -1, 0, 1 or 1.5.
    """

    def hand_score(cards: list[int]) -> int:
        """Calculate the best possible score for a hand in Blackjack."""
        score = sum(cards)
        aces = cards.count(1)
        while aces > 0 and score + 10 <= 21:
            score += 10
            aces -= 1
        return score

    def is_natural_blackjack(cards: list[int]) -> bool:
        """Check if the hand is a natural Blackjack (Ace + 10-value card, 2 cards)."""
        return len(cards) == 2 and set(cards) == {1, 10}
    player_score = hand_score(player)
    dealer_score = hand_score(dealer)
    if player_score > 21:
        return -1.0
    if dealer_score > 21:
        return 1.5 if is_natural_blackjack(player) else 1.0
    if player_score > dealer_score:
        return 1.5 if is_natural_blackjack(player) else 1.0
    elif player_score < dealer_score:
        return -1.0
    else:
        return 0.0

import numpy as np
def average_pool2d(X: np.ndarray, pool_shape: tuple[int, int], stride: tuple[int, int], accum_grad: np.ndarray) -> tuple[list, list]:
    """Performs forward and backward passes of a 2-D average-pooling layer.

    The function must return a tuple (pooled_output, grad_input), both rounded
    to the nearest 4-th decimal and converted to regular Python lists using
    `tolist()`.
    """
    (N, C, H, W) = X.shape
    (p_h, p_w) = pool_shape
    (s_h, s_w) = stride
    out_h = (H - p_h) // s_h + 1
    out_w = (W - p_w) // s_w + 1
    pooled_output = np.zeros((N, C, out_h, out_w))
    for n in range(N):
        for c in range(C):
            for i in range(0, H - p_h + 1, s_h):
                for j in range(0, W - p_w + 1, s_w):
                    window = X[n, c, i:i + p_h, j:j + p_w]
                    pooled_output[n, c, i // s_h, j // s_w] = np.mean(window)
    grad_input = np.zeros_like(X)
    for n in range(N):
        for c in range(C):
            for i in range(0, H - p_h + 1, s_h):
                for j in range(0, W - p_w + 1, s_w):
                    grad = accum_grad[n, c, i // s_h, j // s_w]
                    grad_input[n, c, i:i + p_h, j:j + p_w] += grad / (p_h * p_w)
    pooled_output = np.around(pooled_output, 4).tolist()
    grad_input = np.around(grad_input, 4).tolist()
    return (pooled_output, grad_input)

