from dataclasses import dataclass
@dataclass
class Leaf:
    """A terminal node that stores a prediction value."""
    value: object
@dataclass
class Node:
    """An internal decision-tree node.

    Attributes:
        feature (int):   Index of the feature to test.
        threshold (float): Threshold that splits the data.
        left (Node | Leaf):  Sub-tree for samples with feature value < threshold.
        right (Node | Leaf): Sub-tree for samples with feature value ≥ threshold.
    """
    feature: int
    threshold: float
    left: object
    right: object
def compare_trees(tree_a, tree_b):
    """Recursively checks whether *tree_a* and *tree_b* are equivalent.

    Args:
        tree_a: Root node of the first decision tree (Node or Leaf).
        tree_b: Root node of the second decision tree (Node or Leaf).

    Returns:
        True if the two trees are equivalent, False otherwise.
    """
    if type(tree_a) != type(tree_b):
        return False
    if isinstance(tree_a, Leaf):
        return np.allclose(tree_a.value, tree_b.value, atol=1e-08)
    if isinstance(tree_a, Node):
        if tree_a.feature != tree_b.feature or not np.allclose(tree_a.threshold, tree_b.threshold, atol=1e-08):
            return False
        return compare_trees(tree_a.left, tree_b.left) and compare_trees(tree_a.right, tree_b.right)
    return True

import numpy as np
def mel_filterbank(N: int, n_filters: int=20, fs: int=44000, min_freq: int=0, max_freq: int | None=None, normalize: bool=True) -> list[list[float]]:
    """Build a Mel filterbank transformation matrix.

    The returned matrix has *n_filters* rows and *N//2 + 1* columns.  Each row
    is a triangular filter defined on the Mel scale.
    """
    if max_freq is None:
        max_freq = fs / 2

    def hz_to_mel(f: float) -> float:
        return 2595 * np.log10(1 + f / 700.0)

    def mel_to_hz(mel: float) -> float:
        return 700 * (10 ** (mel / 2595.0) - 1)
    low_mel = hz_to_mel(min_freq)
    high_mel = hz_to_mel(max_freq)
    mel_points = np.linspace(low_mel, high_mel, n_filters + 2)
    freq_points = mel_to_hz(mel_points)
    n_bins = N // 2 + 1
    bin_frequencies = np.arange(n_bins) * fs / N
    filterbank = np.zeros((n_filters, n_bins))
    for i in range(n_filters):
        (f_left, f_center, f_right) = (freq_points[i], freq_points[i + 1], freq_points[i + 2])
        for k in range(n_bins):
            f_k = bin_frequencies[k]
            if f_k < f_center:
                slope = (f_k - f_left) / (f_center - f_left)
            else:
                slope = (f_right - f_k) / (f_right - f_center)
            if f_left < f_k < f_right:
                filterbank[i, k] = slope
    if normalize:
        for i in range(n_filters):
            (f_left, f_center, f_right) = (freq_points[i], freq_points[i + 1], freq_points[i + 2])
            mel_left = hz_to_mel(f_left)
            mel_right = hz_to_mel(f_right)
            width_mel = mel_right - mel_left
            filterbank[i, :] *= 2 / width_mel
    return [list(row.round(4)) for row in filterbank]
