from collections import Counter, defaultdict
def build_fp_tree(transactions: list[list[str]], min_support: int) -> dict:
    """Construct an FP-Tree using only nested dictionaries.

    Parameters
    ----------
    transactions : list[list[str]]
        A list where each element is a list of items representing one transaction.
    min_support : int
        Minimum number of transactions an item has to appear in to be kept.

    Returns
    -------
    dict
        The root node of the FP-Tree.  Each node contains two keys:
        "support" and "children" (the latter mapping item → child-node).
    """
    item_counts = Counter()
    for transaction in transactions:
        item_counts.update(set(transaction))
    frequent_items = {item: count for (item, count) in item_counts.items() if count >= min_support}
    if not frequent_items:
        return {'support': 0, 'children': {}}
    sorted_items = sorted(frequent_items.keys(), key=lambda x: (-frequent_items[x], x))
    item_ordering = {item: idx for (idx, item) in enumerate(sorted_items)}

    def sort_transaction(transaction):
        filtered = [item for item in set(transaction) if item in frequent_items]
        return sorted(filtered, key=lambda x: item_ordering[x])
    root = {'support': 0, 'children': {}}
    for transaction in transactions:
        ordered_items = sort_transaction(transaction)
        if not ordered_items:
            continue
        current = root
        for item in ordered_items:
            if item in current['children']:
                current['children'][item]['support'] += 1
            else:
                current['children'][item] = {'support': 1, 'children': {}}
            current = current['children'][item]
    return root

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def check_space(space: Space, is_tuple: bool):
        if is_tuple:
            if isinstance(space, Tuple):
                return all((isinstance(subspace, Box) for subspace in space.spaces))
            elif isinstance(space, Dict):
                return all((isinstance(subspace, Box) for subspace in space.spaces.values()))
            else:
                raise ValueError('Composite flag set, but the space is not a Tuple or Dict')
        else:
            return isinstance(space, Box)
    cont_action = check_space(env.action_space, tuple_action)
    cont_obs = check_space(env.observation_space, tuple_obs)
    return (cont_action, cont_obs)

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in [0, 1, 2]:
        return -1
    is_scalar = np.isscalar(x)
    x_array = np.array(x, dtype=np.float64) if not is_scalar else np.array([x], dtype=np.float64)
    sigma = 1 / (1 + np.exp(-x_array))
    if order == 0:
        result = sigma
    elif order == 1:
        result = sigma * (1 - sigma)
    elif order == 2:
        result = sigma * (1 - sigma) * (1 - 2 * sigma)
    result = np.round(result, 4)
    if is_scalar:
        return float(result[0])
    else:
        return result.tolist()

import itertools
from collections import defaultdict
def apriori_frequent_itemsets(transactions: list[list], min_sup: float) -> list[tuple]:
    """Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list[Hashable]]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
    """
    if not 0 < min_sup <= 1:
        raise ValueError('min_sup must be in the range (0, 1]')
    num_transactions = len(transactions)
    min_sup_count = int(min_sup * num_transactions)
    item_counts = defaultdict(int)
    for tx in transactions:
        for item in set(tx):
            item_counts[frozenset([item])] += 1
    frequent_itemsets = []
    for (itemset, count) in sorted(item_counts.items()):
        if count >= min_sup_count:
            frequent_itemsets.append(tuple(sorted(itemset)))
    result_by_length = {1: frequent_itemsets}
    result_flat = []
    k = 2
    while True:
        if k - 1 not in result_by_length or not result_by_length[k - 1]:
            break
        candidates = set()
        for itemset1 in result_by_length[k - 1]:
            for itemset2 in result_by_length[k - 1]:
                if itemset1[:-1] == itemset2[:-1] and itemset1[-1] < itemset2[-1]:
                    candidates.add(tuple(sorted(set(itemset1) | set(itemset2))))
        pruned_candidates = []
        for candidate in candidates:
            is_valid = True
            for subset in itertools.combinations(candidate, k - 1):
                subset_set = frozenset(subset)
                if not any((sorted(subset_set) == sorted(s) for s in result_by_length[k - 1])):
                    is_valid = False
                    break
            if is_valid:
                pruned_candidates.append(tuple(sorted(candidate)))
        candidate_support = defaultdict(int)
        for tx in transactions:
            tx_set = set(tx)
            for candidate in pruned_candidates:
                if set(candidate).issubset(tx_set):
                    candidate_support[candidate] += 1
        frequent_k = []
        for (candidate, count) in sorted(candidate_support.items()):
            if count >= min_sup_count:
                frequent_k.append(candidate)
        if not frequent_k:
            break
        result_by_length[k] = frequent_k
        k += 1
    result_flat = []
    for length in sorted(result_by_length):
        result_flat.extend(sorted(result_by_length[length]))
    return result_flat
