def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if root.y is not None:
        return root.y
    if task_type == 'classification':
        feature_value = features[root.label]
        for (i, child) in enumerate(root.child):
            if child.x == feature_value:
                return predict_sample(child, features, task_type)
        return predict_sample(root.child[1], features, task_type)
    elif task_type == 'regression':
        feature_value = features[root.label]
        if feature_value <= root.s:
            return predict_sample(root.child[0], features, task_type)
        else:
            return predict_sample(root.child[1], features, task_type)

import numpy as np
import math
from typing import List
from collections import deque
def detect_outliers(data: np.ndarray, n_trees: int, sample_size: int, epsilon: float) -> List[int]:
    """Tiny Isolation Forest implementation.

    Args:
        data: 2-D NumPy array where each row is an observation and each column a feature.
        n_trees: Number of random isolation trees that will be grown.
        sample_size: Number of samples drawn (without replacement) to grow every tree.
        epsilon: Fraction (0 ≤ ε < 1) of observations that are considered outliers.

    Returns:
        A list with the zero-based indices of the detected outliers, sorted increasingly.
    """
    np.random.seed(42)
    (n_samples, n_features) = data.shape
    if epsilon == 0:
        return []

    def c(n):
        if n <= 1:
            return 0
        return 2 * math.log(n - 1) + 0.5772156649 - 2 * (n - 1) / n

    def phi(n):
        if n == 1:
            return 0
        return 2 * math.log(n - 1) - 2 * (n - 1) / n

    class Node:

        def __init__(self, left=None, right=None, feature=None, threshold=None, indices=None):
            self.left = left
            self.right = right
            self.feature = feature
            self.threshold = threshold
            self.indices = indices

    def build_tree(X, indices, depth=0):
        n = len(indices)
        if n <= 1 or depth >= height:
            return Node(indices=indices)
        feature = np.random.randint(n_features)
        feature_values = X[indices, feature]
        threshold = np.random.uniform(np.min(feature_values), np.max(feature_values))
        left_indices = indices[feature_values <= threshold]
        right_indices = indices[feature_values > threshold]
        left = build_tree(X, left_indices, depth + 1)
        right = build_tree(X, right_indices, depth + 1)
        return Node(left=left, right=right, feature=feature, threshold=threshold, indices=indices)

    def path_length(tree, x_indices_map, x_index):
        path_len = 0
        current_node = tree
        while current_node.left and current_node.right:
            feature_val = x_indices_map[current_node.feature][x_index]
            if feature_val <= current_node.threshold:
                current_node = current_node.left
            else:
                current_node = current_node.right
            path_len += 1
        n_samples_in_leaf = len(current_node.indices)
        return path_len + c(n_samples_in_leaf)
    x_indices_map = {i: data[:, i] for i in range(n_features)}
    if sample_size >= n_samples:
        height = int(math.ceil(math.log2(n_samples)))
    else:
        height = int(math.ceil(math.log2(sample_size)))
    average_path_lengths = np.zeros(n_samples)
    for _ in range(n_trees):
        if sample_size >= n_samples:
            indices = np.arange(n_samples)
        else:
            indices = np.random.choice(n_samples, size=sample_size, replace=False)
        tree = build_tree(data, indices)
        for x_index in range(n_samples):
            average_path_lengths[x_index] += path_length(tree, x_indices_map, x_index)
    average_path_lengths /= n_trees
    if sample_size >= n_samples:
        n_for_phi = n_samples
    else:
        n_for_phi = sample_size
    phi_value = phi(n_for_phi)
    scores = 2 ** (-average_path_lengths / phi_value)
    sorted_indices = np.argsort(scores)[::-1]
    threshold_index = int(epsilon * n_samples)
    outliers = sorted_indices[:threshold_index]
    return sorted(outliers)
