from typing import List, Dict, Tuple, Hashable
def find_frequent_itemsets(transactions: List[List[Hashable]], minsup: float) -> Dict[Tuple[Hashable, ...], int]:
    """Return all frequent itemsets in **transactions**.

    Args:
        transactions: List where each element represents a transaction and is
                       itself a list of items (hashable Python objects).
        minsup:       Minimum support given as a fraction between 0 and 1.

    Returns:
        A dictionary that maps every frequent itemsets (stored as a tuple of
        sorted items) to its absolute support count. If no itemset reaches the
        threshold, return an empty dictionary.
    """
    N = len(transactions)
    min_support_count = math.ceil(minsup * N)
    items = set()
    for t in transactions:
        items.update(t)
    frequent_items = {}
    for item in items:
        support = sum((1 for t in transactions if item in t))
        if support >= min_support_count:
            frequent_items[frozenset([item])] = support
    result = {}
    for key in frequent_items:
        result[tuple(sorted(key))] = frequent_items[key]
    current_frequent = list(frequent_items.keys())
    k = 2
    while current_frequent:
        candidate_itemsets = set()
        for i in range(len(current_frequent)):
            for j in range(i + 1, len(current_frequent)):
                i1 = current_frequent[i]
                i2 = current_frequent[j]
                union = i1.union(i2)
                if len(union) == k and all((frozenset(sub) in frequent_items for sub in combinations(union, k - 1))):
                    candidate_itemsets.add(union)
        new_frequent = {}
        for candidate in candidate_itemsets:
            support = 0
            for t in transactions:
                if candidate.issubset(set(t)):
                    support += 1
            if support >= min_support_count:
                new_frequent[candidate] = support
        for key in new_frequent:
            result[tuple(sorted(key))] = new_frequent[key]
        current_frequent = list(new_frequent.keys())
        k += 1
    return result
