import numpy as np
def softplus(x, deriv: bool=False):
    """Compute the SoftPlus activation function or its derivative (sigmoid).

    Args:
        x: A scalar, list, or NumPy array of real values.
        deriv: A boolean flag. If True, return the derivative (sigmoid of x).

    Returns:
        A Python list of results (rounded to 4 decimal places).
        If input is a scalar, returns a rounded float.
    """
    is_scalar = np.isscalar(x)
    x_arr = np.array(x, dtype=float)
    if deriv:
        result = 1 / (1 + np.exp(-x_arr))
    else:
        result = np.where(x_arr >= 0, np.log1p(np.exp(-x_arr)) + x_arr, np.log1p(np.exp(x_arr)))
    result = np.round(result, 4)
    if is_scalar:
        return float(result)
    else:
        return result.tolist()

