def unsmoothed_ngram_log_prob(corpus: list[str], sequence: list[str], N: int) -> float:
    """Compute the unsmoothed MLE N-gram log-probability of *sequence*.

    Args:
        corpus: List of word tokens used to train the N-gram model.
        sequence: List of word tokens for which to compute the log-probability.
        N: The order of the N-gram model (e.g., 1 for unigram, 2 for bigram).

    Returns:
        The log-probability of the sequence under the N-gram model,
        rounded to 4 decimal places. Returns -inf if any required count is zero.
    """
    if N < 1:
        raise ValueError('N must be at least 1')
    if len(sequence) < N:
        return float('-inf')
    ngram_counts = Counter()
    context_counts = Counter() if N > 1 else None

    def get_ngrams(tokens, n):
        for i in range(len(tokens) - n + 1):
            yield tuple(tokens[i:i + n])
    for ngram in get_ngrams(corpus, N):
        ngram_counts[ngram] += 1
        if N > 1:
            context = ngram[:-1]
            context_counts[context] += 1
    log_prob = 0.0
    for i in range(len(sequence) - N + 1):
        ngram = tuple(sequence[i:i + N])
        ngram_count = ngram_counts.get(ngram, 0)
        if N > 1:
            context = ngram[:-1]
            context_count = context_counts.get(context, 0)
        else:
            context_count = len(corpus)
        if ngram_count == 0 or context_count == 0:
            return float('-inf')
        log_prob += np.log(ngram_count / context_count)
    return round(log_prob, 4)
