def gini_impurity(y: np.ndarray) -> float:
    """Calculate the Gini impurity for a vector of class labels."""
    (classes, counts) = np.unique(y, return_counts=True)
    impurity = 1.0 - np.sum((counts / len(y)) ** 2)
    return impurity
def best_split(X: np.ndarray, y: np.ndarray) -> Tuple[Optional[int], Optional[float]]:
    """Find the best split (feature, threshold) by Gini gain."""
    best_gini = float('inf')
    best_feature = None
    best_threshold = None
    n_features = X.shape[1]
    for feature_idx in range(n_features):
        unique_values = np.unique(X[:, feature_idx])
        if len(unique_values) == 1:
            continue
        sorted_indices = np.argsort(X[:, feature_idx])
        y_sorted = y[sorted_indices]
        thresholds_to_check = []
        for i in range(1, len(sorted_indices)):
            if X[sorted_indices[i], feature_idx] != X[sorted_indices[i - 1], feature_idx]:
                threshold = (X[sorted_indices[i], feature_idx] + X[sorted_indices[i - 1], feature_idx]) / 2
                thresholds_to_check.append(threshold)
        for threshold in thresholds_to_check:
            left_mask = X[:, feature_idx] < threshold
            left_y = y[left_mask]
            right_y = y[~left_mask]
            n_left = len(left_y)
            n_right = len(right_y)
            if n_left == 0 or n_right == 0:
                continue
            gini_left = gini_impurity(left_y)
            gini_right = gini_impurity(right_y)
            weighted_gini = n_left / len(y) * gini_left + n_right / len(y) * gini_right
            if weighted_gini < best_gini:
                best_gini = weighted_gini
                best_feature = feature_idx
                best_threshold = threshold
    return (best_feature, best_threshold)
class DecisionTreeNode:

    def __init__(self, feature: Optional[int]=None, threshold: Optional[float]=None, left: Optional['DecisionTreeNode']=None, right: Optional['DecisionTreeNode']=None, label: Optional[int]=None, depth: int=0):
        self.feature = feature
        self.threshold = threshold
        self.left = left
        self.right = right
        self.label = label
        self.depth = depth

    def predict(self, x: np.ndarray) -> int:
        """Predict class for a single sample."""
        if self.label is not None:
            return self.label
        if x[self.feature] < self.threshold:
            return self.left.predict(x)
        else:
            return self.right.predict(x)
def build_tree(X: np.ndarray, y: np.ndarray, depth: int=0, max_depth: Optional[int]=None) -> DecisionTreeNode:
    """Recursively build the decision tree."""
    (labels, counts) = np.unique(y, return_counts=True)
    if len(labels) == 1:
        return DecisionTreeNode(label=labels[0], depth=depth)
    if max_depth is not None and depth >= max_depth:
        return DecisionTreeNode(label=labels[np.argmax(counts)], depth=depth)
    (best_feature, best_threshold) = best_split(X, y)
    if best_feature is None:
        return DecisionTreeNode(label=labels[np.argmax(counts)], depth=depth)
    left_mask = X[:, best_feature] < best_threshold
    (X_left, y_left) = (X[left_mask], y[left_mask])
    (X_right, y_right) = (X[~left_mask], y[~left_mask])
    if len(y_left) == 0 or len(y_right) == 0:
        return DecisionTreeNode(label=labels[np.argmax(counts)], depth=depth)
    left = build_tree(X_left, y_left, depth + 1, max_depth)
    right = build_tree(X_right, y_right, depth + 1, max_depth)
    return DecisionTreeNode(feature=best_feature, threshold=best_threshold, left=left, right=right, depth=depth)
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: Optional[int]=None) -> List[int]:
    """Build decision tree and predict labels for the test set."""
    tree = build_tree(X_train, y_train, max_depth=max_depth)
    return [tree.predict(x) for x in X_test]
