from collections import Counter, defaultdict
from typing import List
def ngram_log_prob(corpus: List[str], query: str, N: int) -> float:
    """Return the total log-probability of *query* under an N-gram MLE model.

    Parameters
    ----------
    corpus : list[str]
        A list of training sentences. Each sentence is a plain string; tokens
        are assumed to be separated by white-spaces.
    query : str
        The sentence whose probability should be calculated.
    N : int
        The order of the N-gram model (N ≥ 1).

    Returns
    -------
    float
        The sum of natural logarithms of the probabilities of every
        consecutive N-gram occurring in *query*, rounded to 4 decimal places.
        If any necessary N-gram is unseen in the corpus the function returns
        float('-inf').
    """
    if N < 1:
        raise ValueError('N must be at least 1.')

    def tokenize(sentence: str) -> List[str]:
        return sentence.strip().split()
    ngrams = defaultdict(Counter)
    context_counts = Counter()
    for sentence in corpus:
        tokens = tokenize(sentence)
        if N == 1:
            for token in tokens:
                ngrams[tuple()].update([tuple([token])])
            context_counts[tuple()] = len(tokens)
        else:
            padded_tokens = ['<bol>'] * (N - 1) + tokens + ['<eol>']
            for i in range(N - 1, len(padded_tokens)):
                context = tuple(padded_tokens[i - N + 1:i])
                word = padded_tokens[i]
                ngrams[context].update([tuple(context + [word])])
                context_counts[context] += 1
    query_tokens = tokenize(query)
    if N == 1:
        padded_query = query_tokens
    else:
        if len(query_tokens) < N - 1:
            return float('-inf')
        padded_query = ['<bol>'] * (N - 1) + query_tokens + ['<eol>']
    total_log_prob = 0.0
    for i in range(N - 1, len(padded_query)):
        context = tuple(padded_query[i - N + 1:i])
        word = padded_query[i]
        ngram = context + (word,)
        if N > 1 and word == '<eol>':
            continue
        if context not in context_counts:
            return float('-inf')
        ngram_count = ngrams[context].get(tuple(ngram), 0)
        if ngram_count == 0:
            return float('-inf')
        if N == 1:
            total_tokens = context_counts[tuple()]
            prob = ngram_count / total_tokens
        else:
            prob = ngram_count / context_counts[context]
        total_log_prob += math.log(prob)
    return round(total_log_prob, 4)
