import numpy as np
def spectral_clustering(X: np.ndarray, k: int) -> list[int]:
    """Cluster data using Spectral Clustering.

    Args:
        X: 2-D NumPy array where each row is a data sample.
        k: Desired number of clusters.

    Returns:
        List of length n_samples containing an integer cluster label for each sample.
    """
    (n_samples, d) = X.shape
    W = np.zeros((n_samples, n_samples))
    for i in range(n_samples):
        for j in range(n_samples):
            if i != j:
                dist = np.linalg.norm(X[i] - X[j])
                W[i, j] = 1 / (1 + dist)
    D = np.diag(W.sum(axis=1))
    L = D - W
    (eigenvalues, eigenvectors) = np.linalg.eigh(L)
    E = eigenvectors[:, :k]

    def euclidean_distance(a, b):
        return np.sqrt(np.sum((a - b) ** 2))

    def assign_clusters(points, centroids):
        n_points = points.shape[0]
        n_centroids = centroids.shape[0]
        labels = np.zeros(n_points, dtype=int)
        for i in range(n_points):
            min_dist = float('inf')
            for j in range(n_centroids):
                dist = euclidean_distance(points[i], centroids[j])
                if dist < min_dist:
                    min_dist = dist
                    labels[i] = j
        return labels

    def update_centroids(points, labels, centroids):
        n_centroids = centroids.shape[0]
        new_centroids = np.zeros_like(centroids)
        count = np.zeros(n_centroids, dtype=int)
        for i in range(points.shape[0]):
            label = labels[i]
            new_centroids[label] += points[i]
            count[label] += 1
        for j in range(n_centroids):
            if count[j] > 0:
                new_centroids[j] = new_centroids[j] / count[j]
            else:
                new_centroids[j] = points[np.random.choice(points.shape[0])]
        return new_centroids
    centroids = E[:k]
    max_iter = 100
    for _ in range(max_iter):
        labels = assign_clusters(E, centroids)
        new_centroids = update_centroids(E, labels, centroids)
        if np.array_equal(centroids, new_centroids):
            break
        centroids = new_centroids
    return labels.tolist()
