from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def is_space_continuous(space: Space, is_composite: bool):
        if not is_composite:
            return isinstance(space, Box)
        else:
            if isinstance(space, Tuple):
                sub_spaces = space.spaces
            elif isinstance(space, Dict):
                sub_spaces = space.spaces.values()
            else:
                return False
            return all((isinstance(sub, Box) for sub in sub_spaces))
    cont_action = is_space_continuous(env.action_space, tuple_action)
    cont_obs = is_space_continuous(env.observation_space, tuple_obs)
    return (cont_action, cont_obs)

import numpy as np
def gbdt_regression(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_estimators: int=10, learning_rate: float=0.1, max_depth: int=3) -> list[float]:
    """Gradient Boosting Decision Tree (GBDT) regressor."""
    (n_samples, n_features) = X_train.shape
    m_samples = X_test.shape[0]
    F = np.full(n_samples, np.mean(y_train))
    test_predictions = np.full(m_samples, np.mean(y_train))
    for _ in range(n_estimators):
        residuals = y_train - F
        tree = CARTRegressionTree(max_depth)
        tree.fit(X_train, residuals)
        F += learning_rate * tree.predict(X_train)
        test_predictions += learning_rate * tree.predict(X_test)
    return [round(pred, 4) for pred in test_predictions]
class CARTRegressionTree:

    def __init__(self, max_depth):
        self.max_depth = max_depth
        self.tree = None

    def fit(self, X, y):
        """Fit the regression tree using mean-squared-error as the split criterion."""
        self.n_features = X.shape[1]
        self.tree = self._grow_tree(X, y)

    def _grow_tree(self, X, y, depth=0):
        """Recursively grow the tree."""
        n_samples = X.shape[0]
        if depth >= self.max_depth or self._is_pure(y):
            return {'type': 'leaf', 'value': np.mean(y)}
        (best_feature, best_threshold) = self._find_best_split(X, y)
        if best_feature is None:
            return {'type': 'leaf', 'value': np.mean(y)}
        left_mask = X[:, best_feature] < best_threshold
        right_mask = X[:, best_feature] >= best_threshold
        left = self._grow_tree(X[left_mask], y[left_mask], depth + 1)
        right = self._grow_tree(X[right_mask], y[right_mask], depth + 1)
        return {'type': 'internal', 'feature': best_feature, 'threshold': best_threshold, 'left': left, 'right': right}

    def _is_pure(self, y):
        """Check if all samples have the same class."""
        return len(np.unique(y)) == 1

    def _find_best_split(self, X, y):
        """Find the best feature and threshold to split on using MSE."""
        (n_samples, n_features) = X.shape
        best_mse = float('inf')
        (best_feature, best_threshold) = (None, None)
        for feature in range(n_features):
            thresholds = np.unique(X[:, feature])
            for threshold in thresholds:
                left_mask = X[:, feature] < threshold
                right_mask = X[:, feature] >= threshold
                if np.sum(left_mask) == 0 or np.sum(right_mask) == 0:
                    continue
                (y_left, y_right) = (y[left_mask], y[right_mask])
                mse = (np.var(y_left) * len(y_left) + np.var(y_right) * len(y_right)) / n_samples
                if mse < best_mse:
                    best_mse = mse
                    best_feature = feature
                    best_threshold = threshold
        return (best_feature, best_threshold)

    def predict(self, X):
        """Predict using the regression tree."""
        return np.array([self._predict_sample(x, self.tree) for x in X])

    def _predict_sample(self, x, tree):
        """Predict a single sample."""
        if tree['type'] == 'leaf':
            return tree['value']
        if x[tree['feature']] < tree['threshold']:
            return self._predict_sample(x, tree['left'])
        else:
            return self._predict_sample(x, tree['right'])

import numpy as np
def bayesian_linear_regression_pred(X: np.ndarray, y: np.ndarray, X_new: np.ndarray, mu: float | np.ndarray=0.0, sigma: float=1.0, V: float | np.ndarray | None=None, fit_intercept: bool=True) -> list[float]:
    """Bayesian linear regression with known variance.

    The function must return the MAP predictive means for every row in
    `X_new`.  See the task description for full details.
    """
    if fit_intercept:
        X = np.hstack([np.ones((X.shape[0], 1)), X])
        X_new = np.hstack([np.ones((X_new.shape[0], 1)), X_new])
    M = X.shape[1]
    if V is None:
        V = np.eye(M)
    elif isinstance(V, float) or isinstance(V, int):
        V = V * np.eye(M)
    elif isinstance(V, np.ndarray) and V.ndim == 1:
        V = np.diag(V)
    try:
        np.linalg.cholesky(V)
    except np.linalg.LinAlgError:
        raise ValueError('V must be symmetric and positive definite.')
    V_inv = np.linalg.inv(V)
    XtX = X.T @ X
    Sigma_p_inv = V_inv + XtX
    Sigma_p = np.linalg.inv(Sigma_p_inv)
    if np.isscalar(mu):
        mu_vec = np.full(M, mu)
    else:
        if mu.shape[0] != M:
            raise ValueError(f'mu must be a scalar or a vector of length {M}')
        mu_vec = mu
    mu_p = Sigma_p @ (V_inv @ mu_vec + X.T @ y)
    y_pred = X_new @ mu_p
    return [float(np.round(val, 4)) for val in y_pred]

