import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if order not in {0, 1, 2}:
        return -1
    x_array = np.array(x, dtype=np.float64)
    sig = 1 / (1 + np.exp(-x_array))
    if order == 0:
        result = sig
    elif order == 1:
        result = sig * (1 - sig)
    elif order == 2:
        result = sig * (1 - sig) * (1 - 2 * sig)
    result = np.round(result, 4)
    if x_array.ndim == 0:
        return float(result.item())
    else:
        return result.tolist()

import itertools
from collections import defaultdict
def apriori_frequent_itemsets(transactions: list[list], min_sup: float) -> list[tuple]:
    """Find frequent itemsets with the Apriori algorithm.

    Parameters
    ----------
    transactions : list[list[Hashable]]
        List of transactions; each transaction is itself a list of items.
    min_sup : float
        Minimum support threshold expressed as a fraction (>0 and ≤1).

    Returns
    -------
    list[tuple]
        Frequent itemsets ordered by length and then lexicographically.
    """
    if not 0 < min_sup <= 1:
        raise ValueError('min_sup must be in (0, 1]')
    N = len(transactions)
    single_item_counts = defaultdict(int)
    for transaction in transactions:
        for item in set(transaction):
            single_item_counts[frozenset([item])] += 1
    L1 = {itemset: count for (itemset, count) in single_item_counts.items() if count / N >= min_sup}
    frequent_itemsets = list(L1.keys())
    k = 2
    while L1:
        Ck = set()
        Lk_prev = list(L1.keys())
        for i in range(len(Lk_prev)):
            for j in range(i + 1, len(Lk_prev)):
                l1 = set(Lk_prev[i])
                l2 = set(Lk_prev[j])
                if len(l1.union(l2)) == k:
                    candidate = tuple(sorted(l1.union(l2)))
                    Ck.add(candidate)
        candidates = []
        for candidate in Ck:
            is_valid = True
            for subset in itertools.combinations(candidate, k - 1):
                subset_set = frozenset(subset)
                if subset_set not in L1:
                    is_valid = False
                    break
            if is_valid:
                candidates.append(frozenset(candidate))
        candidate_counts = defaultdict(int)
        for transaction in transactions:
            trans_set = set(transaction)
            for candidate in candidates:
                if candidate.issubset(trans_set):
                    candidate_counts[candidate] += 1
        Lk = {itemset: count for (itemset, count) in candidate_counts.items() if count / N >= min_sup}
        if Lk:
            for itemset in Lk:
                frequent_itemsets.append(tuple(sorted(itemset)))
        else:
            break
        L1 = Lk
        k += 1
    frequent_itemsets.sort(key=lambda x: (len(x), x))
    return frequent_itemsets
