import numpy as np
def regression_tree_predict(X_train: list[float | int], y_train: list[float | int], X_test: list[float | int], max_depth: int=3) -> list[float]:
    """Fit a univariate regression tree of depth *max_depth* and predict.

    Parameters
    ----------
    X_train : list[float | int]
        1-D feature values for the training set.
    y_train : list[float | int]
        Continuous target values for the training set.
    X_test : list[float | int]
        1-D feature values for which predictions are required.
    max_depth : int, default 3
        Maximum depth of the binary tree (root has depth 0).

    Returns
    -------
    list[float]
        Predictions for every element in *X_test*.
    """
    if not X_train or not y_train:
        return []

    def sse(y):
        """Compute the sum of squared errors for a list of target values."""
        if len(y) == 0:
            return 0.0
        mean_y = np.mean(y)
        return sum(((y_i - mean_y) ** 2 for y_i in y))

    def build_tree(X, y, depth):
        """Recursively build the regression tree."""
        if depth == max_depth or len(X) < 2:
            return {'type': 'leaf', 'prediction': np.mean(y)}
        best_sse = float('inf')
        best_threshold = None
        best_indices = None
        for (i, threshold) in enumerate(X):
            left_mask = [x <= threshold for x in X]
            right_mask = [x > threshold for x in X]
            if sum(left_mask) < 2 or sum(right_mask) < 2:
                continue
            sse_left = sse([y[i] for i in range(len(X)) if left_mask[i]])
            sse_right = sse([y[i] for i in range(len(X)) if right_mask[i]])
            total_sse = sse_left + sse_right
            if total_sse < best_sse:
                best_sse = total_sse
                best_threshold = threshold
                best_indices = (left_mask, right_mask)
        if best_threshold is None:
            return {'type': 'leaf', 'prediction': np.mean(y)}
        (left_mask, right_mask) = best_indices
        left_X = [X[i] for i in range(len(X)) if left_mask[i]]
        left_y = [y[i] for i in range(len(X)) if left_mask[i]]
        right_X = [X[i] for i in range(len(X)) if right_mask[i]]
        right_y = [y[i] for i in range(len(X)) if right_mask[i]]
        return {'type': 'node', 'threshold': best_threshold, 'left': build_tree(left_X, left_y, depth + 1), 'right': build_tree(right_X, right_y, depth + 1)}

    def predict(tree, x):
        """Predict the target value for a single test point."""
        if tree['type'] == 'leaf':
            return tree['prediction']
        if x <= tree['threshold']:
            return predict(tree['left'], x)
        else:
            return predict(tree['right'], x)
    tree = build_tree(X_train, y_train, 0)
    return [predict(tree, x) for x in X_test]

