import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """

    def process(x):
        if isinstance(x, (float, int)):
            return round(x * (1 - x), 4)
        elif isinstance(x, list):
            return [process(item) for item in x]
        elif isinstance(x, np.ndarray):
            return np.round(x * (1 - x), 4)
        else:
            raise TypeError('Unsupported input type: {}'.format(type(x)))
    if isinstance(z, list) and (not z):
        return []
    return process(z)

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if root.y is not None:
        return root.y
    if task_type == 'classification':
        feature_value = features[root.label]
        for (i, child) in enumerate(root.child):
            if child.x == feature_value:
                return predict_sample(child, features, task_type)
        return predict_sample(root.child[1], features, task_type)
    elif task_type == 'regression':
        feature_value = features[root.label]
        if feature_value <= root.s:
            return predict_sample(root.child[0], features, task_type)
        else:
            return predict_sample(root.child[1], features, task_type)

import math
from collections import defaultdict
import numpy as np
def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    """Train a MaxEnt classifier using GIS and return predicted labels on X_test."""
    num_features = X_train.shape[1]
    num_classes = len(np.unique(y_train))
    class_labels = np.unique(y_train)
    num_train_samples = X_train.shape[0]
    feature_counts = defaultdict(lambda : defaultdict(int))
    feature_label_counts = defaultdict(lambda : defaultdict(lambda : 0))
    label_counts = defaultdict(int)
    for i in range(num_train_samples):
        x_i = X_train[i]
        y_i = y_train[i]
        label_counts[y_i] += 1
        for j in range(num_features):
            if x_i[j] != 0:
                feature_counts[j, x_i[j]] += 1
                feature_label_counts[j, x_i[j], y_i] += 1
    weights = defaultdict(float)
    for (j, v) in feature_counts:
        for y in class_labels:
            weights[j, v, y] = 0.0
    for _ in range(n_iter):
        max_update = 0.0
        for y in class_labels:
            for (j, v) in feature_counts:
                empirical = feature_label_counts[j, v, y] / num_train_samples
                expected = 0.0
                for i in range(num_train_samples):
                    if X_train[i, j] == v:
                        exp_sum = 0.0
                        for y_prime in class_labels:
                            feature_sum = 0.0
                            for j_prime in range(num_features):
                                val = X_train[i, j_prime]
                                if val != 0:
                                    feature_sum += weights[j_prime, val, y_prime]
                            exp_sum += math.exp(feature_sum)
                        feature_sum_y = 0.0
                        for j_prime in range(num_features):
                            val = X_train[i, j_prime]
                            if val != 0:
                                feature_sum_y += weights[j_prime, val, y]
                        prob_y = math.exp(feature_sum_y) / exp_sum
                        expected += prob_y / num_train_samples
                delta = math.log(empirical / expected) if expected != 0 else 0.0
                weights[j, v, y] += delta
                max_update = max(max_update, abs(delta))
        if max_update < epsilon:
            break
    predictions = []
    for x in X_test:
        max_prob = -math.inf
        best_y = None
        for y in class_labels:
            feature_sum = 0.0
            for j in range(num_features):
                val = x[j]
                if val != 0:
                    feature_sum += weights[j, val, y]
            prob = math.exp(feature_sum)
            if prob > max_prob:
                max_prob = prob
                best_y = y
        predictions.append(best_y)
    return predictions
