def softplus(x, deriv: bool=False):
    """Computes the SoftPlus activation function or its derivative (sigmoid).

    Args:
        x: A scalar, list, or NumPy array of real values.
        deriv: If True, computes the derivative of SoftPlus (i.e., the sigmoid function).

    Returns:
        A Python list (or a float for scalar input) of values rounded to the nearest 4th decimal.
    """
    x = np.array(x, dtype=float)
    if deriv:
        return np.round(1 / (1 + np.exp(-np.clip(x, -100, 100))), 4).tolist()
    else:
        return np.round(np.where(x > -30, np.log1p(np.exp(-np.clip(x, -100, 100))) + np.clip(x, -100, 100), np.exp(np.clip(x, -100, 100))), 4).tolist()

