TIME_STEPS = 20
def string_to_int(text: str, time_steps: int, vocabulary: dict[str, int]) -> list[int]:
    """Encodes a string into a fixed-length list of integer tokens using the provided vocabulary."""
    encoded = []
    for char in text:
        if char in vocabulary:
            encoded.append(vocabulary[char])
        else:
            encoded.append(0)
    if len(encoded) < time_steps:
        encoded += [0] * (time_steps - len(encoded))
    else:
        encoded = encoded[:time_steps]
    return encoded
def int_to_string(indices, inverse_vocab: dict[int, str]) -> str:
    """Decodes a list of integer tokens back to a string, skipping padding tokens (0)."""
    return ''.join((inverse_vocab[idx] for idx in indices if idx != 0))
def run_example(model, input_vocabulary: dict[str, int], inv_output_vocabulary: dict[int, str], text: str) -> str:
    """Executes the full pipeline: encode -> model predict -> decode."""
    encoded = string_to_int(text, TIME_STEPS, input_vocabulary)
    encoded = np.array([encoded])
    prediction_logits = model.predict(encoded)
    predicted_ids = np.argmax(prediction_logits, axis=-1).flatten()
    decoded_text = int_to_string(predicted_ids, inv_output_vocabulary)
    return decoded_text
