import numpy as np
def metric(name: str):
    """Factory producing a validated distance function.

    The function creates and returns a callable *d* that computes one of four
    classical distances (Euclidean, Manhattan, Chebyshev, Cosine) between two
    numeric vectors.  All numeric outputs are rounded to four decimal places.

    Validation rules inside the returned callable:
    * Both arguments must be one-dimensional, non-empty, equal-length numeric
      iterables (list, tuple or NumPy array).
    * Metric *name* must be one of the supported strings.
    * For the cosine distance, zero-norm vectors are rejected.

    If the metric name is unsupported or validation fails, *d* returns -1.

    Args:
        name (str): Name of the desired metric.

    Returns:
        Callable[[Iterable, Iterable], float | int]: A distance function with
        integrated validation.
    """
    supported_metrics = ['euclidean', 'manhattan', 'chebyshev', 'cosine']

    def distance(x, y):
        if name not in supported_metrics:
            return -1
        try:
            x = np.asarray(x, dtype=float).ravel()
            y = np.asarray(y, dtype=float).ravel()
        except:
            return -1
        if x.ndim != 1 or y.ndim != 1:
            return -1
        if x.size == 0 or y.size == 0:
            return -1
        if x.shape[0] != y.shape[0]:
            return -1
        if name == 'euclidean':
            return round(np.sqrt(np.sum((x - y) ** 2)), 4)
        elif name == 'manhattan':
            return round(np.sum(np.abs(x - y)), 4)
        elif name == 'chebyshev':
            return round(np.max(np.abs(x - y)), 4)
        elif name == 'cosine':
            dot_product = np.dot(x, y)
            norm_x = np.linalg.norm(x)
            norm_y = np.linalg.norm(y)
            if norm_x == 0 or norm_y == 0:
                return -1
            return round(1 - dot_product / (norm_x * norm_y), 4)
    return distance

import math
from collections import Counter, defaultdict
from typing import List
def ngram_log_prob(corpus: List[str], query: str, N: int) -> float:
    """Return the total log-probability of *query* under an N-gram MLE model.

    Parameters
    ----------
    corpus : list[str]
        A list of training sentences. Each sentence is a plain string; tokens
        are assumed to be separated by white-spaces.
    query : str
        The sentence whose probability should be calculated.
    N : int
        The order of the N-gram model (N ≥ 1).

    Returns
    -------
    float
        The sum of natural logarithms of the probabilities of every
        consecutive N-gram occurring in *query*, rounded to 4 decimal places.
        If any necessary N-gram is unseen in the corpus the function returns
        float('-inf').
    """
    if N < 1:
        raise ValueError('N must be greater than or equal to 1')
    total_tokens = 0
    ngrams_counter = defaultdict(Counter)
    for sentence in corpus:
        tokens = sentence.split()
        total_tokens += len(tokens)
        if N == 1:
            for token in tokens:
                ngrams_counter[tuple()].update([tuple([token])])
        else:
            padded = ['<bol>'] * (N - 1) + tokens + ['<eol>']
            for i in range(len(padded) - N + 1):
                ngram = tuple(padded[i:i + N])
                prefix = tuple(padded[i:i + N - 1])
                ngrams_counter[prefix].update([ngram])
    query_tokens = query.split()
    if len(query_tokens) < N - 1:
        return float('-inf')
    if N == 1:
        log_prob = 0.0
        for token in query_tokens:
            if token in ngrams_counter[tuple()]:
                count = ngrams_counter[tuple()][tuple([token])]
                prob = count / total_tokens
                log_prob += math.log(prob)
            else:
                return float('-inf')
    else:
        log_prob = 0.0
        padded_query = ['<bol>'] * (N - 1) + query_tokens + ['<eol>']
        for i in range(len(padded_query) - N + 1):
            ngram = tuple(padded_query[i:i + N])
            prefix = tuple(padded_query[i:i + N - 1])
            if prefix in ngrams_counter and ngram in ngrams_counter[prefix]:
                count_ngram = ngrams_counter[prefix][ngram]
                count_prefix = sum(ngrams_counter[prefix].values())
                prob = count_ngram / count_prefix
                log_prob += math.log(prob)
            else:
                return float('-inf')
    return round(log_prob, 4)

import numpy as np
def mel_filterbank(N: int, n_filters: int=20, fs: int=44000, min_freq: int=0, max_freq: int | None=None, normalize: bool=True) -> list[list[float]]:
    """Build a Mel filterbank transformation matrix.

    The returned matrix has *n_filters* rows and *N//2 + 1* columns.  Each row
    is a triangular filter defined on the Mel scale.  See the detailed task
    description for the exact algorithm that has to be implemented.
    """
    if max_freq is None:
        max_freq = fs / 2

    def hz_to_mel(f):
        return 2595 * np.log10(1 + f / 700)

    def mel_to_hz(mel):
        return 700 * (10 ** (mel / 2595) - 1)
    mel_min = hz_to_mel(min_freq)
    mel_max = hz_to_mel(max_freq)
    mels = np.linspace(mel_min, mel_max, n_filters + 2)
    freqs = mel_to_hz(mels)
    bin_freqs = np.linspace(0, fs / 2, N // 2 + 1)
    n_bins = N // 2 + 1
    filterbank = np.zeros((n_filters, n_bins))
    for i in range(n_filters):
        f_left = freqs[i]
        f_center = freqs[i + 1]
        f_right = freqs[i + 2]
        for k in range(n_bins):
            f_bin = bin_freqs[k]
            if f_left < f_center and f_bin >= f_left and (f_bin < f_center):
                left_slope = (f_bin - f_left) / (f_center - f_left)
            else:
                left_slope = 0
            if f_center < f_right and f_bin >= f_center and (f_bin < f_right):
                right_slope = (f_right - f_bin) / (f_right - f_center)
            else:
                right_slope = 0
            weight = min(left_slope, right_slope)
            if weight < 0:
                weight = 0
            filterbank[i, k] = weight
    if normalize:
        for i in range(n_filters):
            f_left = freqs[i]
            f_right = freqs[i + 2]
            if f_right - f_left > 0:
                norm_factor = 2 / (f_right - f_left)
                filterbank[i, :] *= norm_factor
    result = [[round(val, 4) for val in row] for row in filterbank.tolist()]
    return result

from typing import Any, Dict, Iterable, Tuple as PyTuple
class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def check_space(space: Space, is_tuple: bool):
        if not is_tuple:
            return isinstance(space, Box)
        elif isinstance(space, Tuple):
            return all((isinstance(sub_space, Box) for sub_space in space.spaces))
        elif isinstance(space, Dict):
            return all((isinstance(sub_space, Box) for sub_space in space.spaces.values()))
        else:
            raise ValueError('Only Tuple or Dict Spaces can be marked as composite.')
    cont_action = check_space(env.action_space, tuple_action)
    cont_obs = check_space(env.observation_space, tuple_obs)
    return (cont_action, cont_obs)

import numpy as np
def selu(x, order: int=0):
    """Compute the SELU activation or its derivatives.

    Parameters
    ----------
    x : float | list | np.ndarray
        Input value(s). Can be a scalar, 1-D list/array, or multi-D list/array.
    order : int, default 0
        0 → SELU(x)
        1 → first derivative d(SELU)/dx
        2 → second derivative d²(SELU)/dx²

    Returns
    -------
    list | float
        A Python list (or scalar for scalar input) containing the element-wise
        result, rounded to 4 decimals.
    """
    alpha = 1.6732632423543772
    scale = 1.0507009873554805
    x = np.array(x, dtype=np.float64, copy=False)
    if order == 0:

        def selu_func(x):
            return np.where(x > 0, scale * x, scale * alpha * (np.exp(x) - 1))
    elif order == 1:

        def selu_func(x):
            return np.where(x > 0, scale, scale * alpha * np.exp(x))
    elif order == 2:

        def selu_func(x):
            return np.where(x > 0, 0, scale * alpha * np.exp(x))
    else:
        raise ValueError('order must be 0, 1, or 2')
    result = selu_func(x)
    result = np.round(result, 4)
    return result.tolist()

import numpy as np
def root_mean_squared_log_error(actual: list[float], predicted: list[float]) -> float:
    """Calculate the Root Mean Squared Logarithmic Error (RMSLE)."""
    if len(actual) != len(predicted):
        return -1
    if not all((x >= 0 for x in actual)) or not all((x >= 0 for x in predicted)):
        return -1
    errors = [np.log1p(p) - np.log1p(a) for (a, p) in zip(actual, predicted)]
    squared_errors = [e ** 2 for e in errors]
    mean_squared_log_error = np.mean(squared_errors)
    rmsle = np.sqrt(mean_squared_log_error)
    return round(rmsle, 4)

import numpy as np
def sigmoid(x, order: int=0):
    """Compute the logistic sigmoid or its derivatives.

    Args:
        x (float | int | list | np.ndarray): Input data. Can be a scalar, list, or NumPy array.
        order (int, optional): 0 = function value, 1 = first derivative,
            2 = second derivative. Defaults to 0.

    Returns:
        float | list: Result rounded to 4 decimals. Scalar input returns a float;
            vector/matrix input returns a Python list preserving the shape.
            If *order* is not 0, 1, or 2, the function returns -1.
    """
    if not isinstance(order, int) or order not in {0, 1, 2}:
        return -1
    x_array = np.array(x, dtype=float)
    sig = 1.0 / (1.0 + np.exp(-x_array))
    if order == 0:
        result = sig
    elif order == 1:
        result = sig * (1 - sig)
    elif order == 2:
        result = sig * (1 - sig) * (1 - 2 * sig)
    result = np.round(result, 4)
    if x_array.ndim == 0:
        return float(result)
    else:
        return result.tolist()

import numpy as np
def rbf_kernel(X: list[list[int | float]], Y: list[list[int | float]] | None=None, sigma: float | list[float] | None=None) -> list[list[float]]:
    """
    Compute the Radial Basis Function (RBF) kernel matrix.

    Parameters:
    - X: First set of vectors (shape N×C)
    - Y: Second set of vectors (shape M×C); defaults to X if None
    - sigma: Bandwidth parameter; can be float, list[float], or None

    Returns:
    - Kernel matrix as a list of lists with entries rounded to 4 decimal places.
    - Returns -1 for invalid input.
    """
    X = np.array(X, dtype=float)
    (N, C) = X.shape
    if Y is None:
        Y = X
    else:
        Y = np.array(Y, dtype=float)
        M = Y.shape[0]
        if Y.shape[1] != C:
            return -1
    if sigma is None:
        sigma = np.sqrt(C / 2)
    elif isinstance(sigma, (int, float)):
        if sigma <= 0:
            return -1
    elif isinstance(sigma, list):
        if len(sigma) != C:
            return -1
        if any((s <= 0 for s in sigma)):
            return -1
    else:
        return -1
    if isinstance(sigma, (int, float)):
        X_norm = X / sigma
        Y_norm = Y / sigma
    else:
        sigma_array = np.array(sigma, dtype=float)
        X_norm = X / sigma_array
        Y_norm = Y / sigma_array
    X_sq = np.sum(X_norm ** 2, axis=1).reshape(-1, 1)
    Y_sq = np.sum(Y_norm ** 2, axis=1).reshape(1, -1)
    cross = X_norm @ Y_norm.T
    distances = X_sq - 2 * cross + Y_sq
    K = np.exp(-0.5 * distances)
    K_rounded = np.round(K, 4).tolist()
    for i in range(len(K_rounded)):
        for j in range(len(K_rounded[i])):
            K_rounded[i][j] = float(np.round(K_rounded[i][j], 4))
    return K_rounded

from collections import Counter, defaultdict, deque
import itertools
def fp_growth(transactions, min_sup):
    """Mine all frequent item-sets using the FP-Growth algorithm.

    Parameters
    ----------
    transactions : Iterable[Iterable[Hashable]]
        A collection of transactions.  Each transaction is an iterable containing hashable items.
    min_sup : int
        Minimum number of occurrences an item-set must have to be considered frequent.

    Returns
    -------
    list[tuple]
        All frequent item-sets sorted 1) by length, 2) lexicographically.  Every
        tuple itself is sorted lexicographically.
    """
    item_counts = Counter()
    for transaction in transactions:
        item_counts.update(transaction)
    frequent_items = {item for (item, count) in item_counts.items() if count >= min_sup}
    if not frequent_items:
        return []
    item_order = sorted(frequent_items, key=lambda x: item_counts[x], reverse=True)
    item_to_index = {item: idx for (idx, item) in enumerate(item_order)}

    def sort_transaction(transaction):
        return [item for item in transaction if item in frequent_items]

    class FPNode:

        def __init__(self, value=None, count=0, parent=None):
            self.value = value
            self.count = count
            self.parent = parent
            self.children = {}
            self.node_link = None

    def update_header(node, header_table):
        while node.node_link is not None:
            node = node.node_link
        node.node_link = header_table[0]
        header_table[0] = node

    def update_tree(items, node, header_table):
        if not items:
            return
        first = items[0]
        rest = items[1:]
        if first in node.children:
            node.children[first].count += 1
        else:
            new_node = FPNode(first, 1, node)
            node.children[first] = new_node
            if header_table[first][0] is None:
                header_table[first][0] = [new_node]
            else:
                update_header(new_node, header_table[first])
        update_tree(rest, node.children[first], header_table)
    header_table = defaultdict(lambda : [None, 0])
    root = FPNode()
    for transaction in transactions:
        sorted_items = sorted(sort_transaction(transaction), key=lambda x: item_to_index[x])
        update_tree(sorted_items, root, header_table)

    def mine_tree(header_table, min_sup, prefix, frequent_itemsets):
        ordered_items = [item for item in header_table if header_table[item][1] >= min_sup]
        ordered_items.sort(key=lambda x: item_counts[x])
        for item in ordered_items:
            new_prefix = tuple(sorted(prefix + (item,)))
            frequent_itemsets.append(new_prefix)
            conditional_patterns = []
            node = header_table[item][0]
            while node is not None:
                prefix_path = []
                current_node = node.parent
                while current_node.value is not None:
                    prefix_path.append((current_node.value, current_node.count))
                    current_node = current_node.parent
                if prefix_path:
                    path_items = [(item, count * node.count) for (item, count) in prefix_path]
                    conditional_patterns.append(path_items)
                node = node.node_link
            if conditional_patterns:
                path_counts = Counter()
                for items in conditional_patterns:
                    for (item, count) in items:
                        path_counts[item] += count
                filtered_path_items = [(item, count) for (item, count) in path_counts.items() if count >= min_sup]
                if filtered_path_items:
                    filtered_path_items.sort(key=lambda x: x[1], reverse=True)
                    conditional_transactions = []
                    for path in conditional_patterns:
                        conditional_path = []
                        for (item, _) in path:
                            if path_counts[item] >= min_sup:
                                conditional_path.append(item)
                        conditional_path.reverse()
                        if conditional_path:
                            conditional_transactions.append(conditional_path)
                    if conditional_transactions:
                        new_header_table = defaultdict(lambda : [None, 0])
                        conditional_root = FPNode()
                        for conditional_transaction in conditional_transactions:
                            sorted_items = [item for item in conditional_transaction if path_counts[item] >= min_sup]
                            update_tree(sorted_items, conditional_root, new_header_table)
                        mine_tree(new_header_table, min_sup, prefix + (item,), frequent_itemsets)
    frequent_itemsets = []
    mine_tree(header_table, min_sup, (), frequent_itemsets)
    for item in frequent_items:
        frequent_itemsets.append(tuple([item]))

    def sort_key(itemset):
        return (len(itemset), itemset)
    return sorted(frequent_itemsets, key=sort_key)

import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """
    arr = np.array(z, dtype=float)
    grad = arr * (1 - arr)
    rounded_grad = np.round(grad, 4)

    def to_list(arr):
        if arr.ndim == 0:
            return float(arr)
        elif arr.ndim == 1:
            return [float(x) for x in arr]
        else:
            return [to_list(row) for row in arr]
    return to_list(rounded_grad)

def predict_sample(root: 'Node', features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """

    def traverse(node):
        if node.y is not None:
            return node.y
        feature_value = features[node.label]
        if task_type == 'classification':
            for child in node.child:
                if child.x == feature_value:
                    return traverse(child)
            return traverse(node.child[1])
        elif task_type == 'regression':
            if feature_value <= node.s:
                return traverse(node.child[0])
            else:
                return traverse(node.child[1])
    return traverse(root)
