import numpy as np
def glm_links():
    """Construct and return standard GLM link functions.

    Returns
    -------
    dict
        A three-entry dictionary (identity, log, logit) where each entry is a
        dictionary containing callables for the link, its inverse and its
        derivative with respect to μ. All functions must work with numpy
        scalars as well as 1-D/2-D numpy arrays via element-wise operations.
    """

    def identity_link(mu):
        return np.array(mu, dtype=float)

    def identity_inverse(eta):
        return np.array(eta, dtype=float)

    def identity_derivative(mu):
        return np.ones_like(mu)
    identity = {'link': identity_link, 'inv_link': identity_inverse, 'deriv': identity_derivative}

    def log_link(mu):
        with np.errstate(divide='ignore'):
            return np.log(mu)

    def log_inverse(eta):
        return np.exp(eta)

    def log_derivative(mu):
        return 1.0 / mu
    log = {'link': log_link, 'inv_link': log_inverse, 'deriv': log_derivative}

    def logit_link(mu):
        with np.errstate(divide='ignore', invalid='ignore'):
            return np.log(mu / (1 - mu))

    def logit_inverse(eta):
        return 1.0 / (1 + np.exp(-eta))

    def logit_derivative(mu):
        return 1.0 / (mu * (1 - mu))
    logit = {'link': logit_link, 'inv_link': logit_inverse, 'deriv': logit_derivative}
    return {'identity': identity, 'log': log, 'logit': logit}

