def sigmoid_activation(x):
    """Compute the sigmoid of *x* and its gradient.

    Parameters
    ----------
    x : float | int | list | numpy.ndarray
        Input data that can be a scalar, a Python list, or a NumPy array.

    Returns
    -------
    tuple
        A tuple (sigmoid_x, gradient_x)
        where each element is rounded to 4 decimal places and returned as:
        • float when *x* is scalar
        • Python list when *x* is array-like
    """
    is_scalar = np.isscalar(x)
    arr = np.array(x, dtype=float) if not is_scalar else x

    def sigmoid(val):
        return 1 / (1 + np.exp(-val))

    def gradient(val):
        s = sigmoid(val)
        return s * (1 - s)
    sigmoid_x = np.vectorize(sigmoid)(arr)
    gradient_x = np.vectorize(gradient)(arr)
    if is_scalar:
        return (round(sigmoid_x, 4), round(gradient_x, 4))
    else:
        return ([round(val, 4) for val in sigmoid_x.flatten()].tolist(), [round(val, 4) for val in gradient_x.flatten()].tolist())

