def gini_impurity(labels: np.ndarray) -> float:
    """Calculate Gini impurity for the given class labels."""
    (_, counts) = np.unique(labels, return_counts=True)
    probabilities = counts / labels.shape[0]
    return 1 - np.sum(probabilities ** 2)
def best_split(X: np.ndarray, y: np.ndarray) -> tuple[Optional[int], Optional[float]]:
    """
    Find the best feature and threshold to split on in the current node.
    Returns (feature_index, threshold) that maximizes Gini gain.
    """
    (n_samples, n_features) = X.shape
    if n_samples <= 1:
        return (None, None)
    parent_gini = gini_impurity(y)
    best_gain = 0
    best_threshold = None
    best_feature = None
    for feature_idx in range(n_features):
        feature_values = X[:, feature_idx]
        unique_values = np.unique(feature_values)
        for i in range(len(unique_values) - 1):
            threshold = (unique_values[i] + unique_values[i + 1]) / 2
            left_mask = feature_values <= threshold
            right_mask = feature_values > threshold
            if np.sum(left_mask) == 0 or np.sum(right_mask) == 0:
                continue
            left_gini = gini_impurity(y[left_mask])
            right_gini = gini_impurity(y[right_mask])
            n_left = np.sum(left_mask)
            n_right = np.sum(right_mask)
            gain = parent_gini - n_left / n_samples * left_gini - n_right / n_samples * right_gini
            if gain > best_gain:
                best_gain = gain
                best_threshold = threshold
                best_feature = feature_idx
    if best_gain == 0:
        return (None, None)
    return (best_feature, best_threshold)
class DecisionTreeNode:

    def __init__(self, feature_index: Optional[int]=None, threshold: Optional[float]=None, left: Optional[Any]=None, right: Optional[Any]=None, value: Optional[int]=None, depth: int=0):
        self.feature_index = feature_index
        self.threshold = threshold
        self.left = left
        self.right = right
        self.value = value
        self.depth = depth

    def predict(self, sample: np.ndarray) -> int:
        """Recursively predict the class label for a single sample."""
        if self.value is not None:
            return self.value
        if sample[self.feature_index] <= self.threshold:
            return self.left.predict(sample)
        else:
            return self.right.predict(sample)
def build_tree(X: np.ndarray, y: np.ndarray, current_depth: int, max_depth: Optional[int]) -> DecisionTreeNode:
    """
    Recursively build a decision tree using CART algorithm.
    """
    if np.all(y == y[0]) or (max_depth is not None and current_depth >= max_depth):
        return DecisionTreeNode(value=int(y[0]), depth=current_depth)
    (feature, threshold) = best_split(X, y)
    if feature is None:
        (unique, counts) = np.unique(y, return_counts=True)
        index = np.argmax(counts)
        return DecisionTreeNode(value=int(unique[index]), depth=current_depth)
    left_mask = X[:, feature] <= threshold
    right_mask = X[:, feature] > threshold
    left_child = build_tree(X[left_mask], y[left_mask], current_depth + 1, max_depth)
    right_child = build_tree(X[right_mask], y[right_mask], current_depth + 1, max_depth)
    return DecisionTreeNode(feature_index=feature, threshold=threshold, left=left_child, right=right_child, depth=current_depth)
def decision_tree_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, max_depth: Optional[int]=None) -> List[int]:
    """Build a CART decision tree on (X_train, y_train) and predict labels for X_test."""
    if X_train.shape[0] != y_train.shape[0]:
        raise ValueError('`X_train` and `y_train` must have the same number of samples.')
    if X_train.shape[1] != X_test.shape[1]:
        raise ValueError('Number of features in `X_train` and `X_test` must match.')
    if X_train.shape[0] == 0 or X_test.shape[0] == 0:
        raise ValueError('Training or test data cannot be empty.')
    root = build_tree(X_train, y_train, 0, max_depth)
    predictions = [root.predict(sample) for sample in X_test]
    return predictions
