class Space:
    pass
class Box(Space):

    def __init__(self, low: float, high: float, shape: PyTuple[int, ...]):
        self.low = low
        self.high = high
        self.shape = shape
class Tuple(Space):

    def __init__(self, spaces: Iterable[Space]):
        self.spaces = tuple(spaces)
class Dict(Space):

    def __init__(self, spaces: Dict[str, Space]):
        self.spaces = dict(spaces)
class Env:
    """Tiny environment that only stores two spaces."""

    def __init__(self, action_space: Space, observation_space: Space):
        self.action_space = action_space
        self.observation_space = observation_space
def is_continuous(env: Env, tuple_action: bool, tuple_obs: bool):
    """Determine whether the given environment's spaces are continuous.

    A space is *continuous* if it is an instance of `Box`. For composite spaces
    (`Tuple` or `Dict`) the space is continuous only if **all** its sub-spaces
    are `Box`.

    Args:
        env:          Environment exposing `action_space` and `observation_space`.
        tuple_action: Whether the *action* space is composite.
        tuple_obs:    Whether the *observation* space is composite.

    Returns:
        A tuple `(cont_action, cont_obs)` of booleans.
    """

    def _is_continuous(space: Space, is_composite: bool) -> bool:
        if is_composite:
            if isinstance(space, (Tuple, Dict)):
                return all((isinstance(subspace, Box) for subspace in space.spaces.values()))
            else:
                return False
        else:
            return isinstance(space, Box)
    cont_action = _is_continuous(env.action_space, tuple_action)
    cont_obs = _is_continuous(env.observation_space, tuple_obs)
    return (cont_action, cont_obs)

import numpy as np
def gbdt_regression(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, n_estimators: int=10, learning_rate: float=0.1, max_depth: int=3) -> list[float]:
    """
    Gradient Boosting Decision Tree (GBDT) regressor implemented from scratch using NumPy.
    """
    F0 = np.mean(y_train)
    F = np.full_like(y_train, F0)
    (n_samples, n_features) = X_train.shape

    class RegressionTree:
        """
        A simple CART regression tree with square loss.
        """

        def __init__(self, max_depth: int, min_samples_split: int=2):
            self.max_depth = max_depth
            self.min_samples_split = min_samples_split
            self.tree = {}

        def fit(self, X: np.ndarray, y: np.ndarray, depth: int=0, node: dict=None) -> dict:
            if node is None:
                node = {'left': None, 'right': None, 'split': None, 'value': None}
            if depth >= self.max_depth or len(y) < self.min_samples_split:
                node['value'] = np.mean(y)
                return node
            (n_features, best_value) = self._best_split(X, y)
            if best_value is None:
                node['value'] = np.mean(y)
                return node
            new_node = {'left': {}, 'right': {}, 'split': best_value, 'value': None}
            left_idx = X[:, n_features] <= best_value
            right_idx = ~left_idx
            node['left'] = self.fit(X[left_idx], y[left_idx], depth + 1, new_node['left'])
            node['right'] = self.fit(X[right_idx], y[right_idx], depth + 1, new_node['right'])
            return new_node

        def _best_split(self, X: np.ndarray, y: np.ndarray):
            (n, n_features) = X.shape
            best_gain = 0
            best_col = -1
            best_val = -1
            for col in range(n_features):
                sort_idx = np.argsort(X[:, col])
                sorted_y = y[sort_idx]
                candidates = np.unique(X[sort_idx, col])
                for val in candidates[1:-1]:
                    left_idx = X[:, col] <= val
                    left_y = y[left_idx]
                    right_y = y[~left_idx]
                    if len(left_y) == 0 or len(right_y) == 0:
                        continue
                    mse_gain = self._mse_gain(y, left_y, right_y)
                    if mse_gain > best_gain:
                        best_gain = mse_gain
                        best_col = col
                        best_val = val
            return (best_col, best_val) if best_gain > 0 else (None, None)

        def _mse_gain(self, y: np.ndarray, y1: np.ndarray, y2: np.ndarray) -> float:
            mse_total = np.mean((y - np.mean(y)) ** 2)
            mse1 = np.mean((y1 - np.mean(y1)) ** 2)
            mse2 = np.mean((y2 - np.mean(y2)) ** 2)
            weight1 = len(y1) / len(y)
            weight2 = len(y2) / len(y)
            gain = mse_total - (weight1 * mse1 + weight2 * mse2)
            return gain

        def predict(self, X: np.ndarray):
            return np.array([self._predict_tree(x, self.tree) for x in X])

        def _predict_tree(self, x: np.ndarray, node: dict):
            if node.get('value') is not None:
                return node['value']
            (col, val) = node['split']
            if x[col] <= val:
                return self._predict_tree(x, node['left'])
            else:
                return self._predict_tree(x, node['right'])
    for _ in range(n_estimators):
        residual = y_train - F
        tree = RegressionTree(max_depth=max_depth)
        tree.fit(X_train, residual)
        pred_delta = tree.predict(X_train)
        F += learning_rate * pred_delta
    final_preds = [F0]
    for _ in range(n_estimators):
        tree = RegressionTree(max_depth=max_depth)
        tree.fit(X_train, y_train - final_preds[-1])
        test_pred = tree.predict(X_test)
        final_preds.append(final_preds[-1] + learning_rate * test_pred)
    test_predictions = final_preds[-1]
    test_predictions = [round(float(pred), 4) for pred in test_predictions.tolist()]
    return test_predictions
