        return (len(itemset), itemset)
    return sorted(frequent_itemsets, key=itemset_sort_key)

import numpy as np
def sigmoid_grad(z):
    """Return the derivative of the sigmoid function given its output.

    The input *z* already contains sigmoid values (numbers in [0,1]).  The
    derivative is computed as z*(1-z) element-wise.

    Args:
        z: float, list or NumPy ndarray of sigmoid outputs.

    Returns:
        Same structure as *z* (float or nested list) with each value replaced
        by its gradient, rounded to 4 decimal places.
    """

    def _process(obj):
        if isinstance(obj, (list, np.ndarray)):
            if len(obj) == 0:
                return []
            return [_process(item) for item in obj]
        elif isinstance(obj, float):
            return round(obj * (1 - obj), 4)
        else:
            return round(float(obj) * (1 - float(obj)), 4)
    if isinstance(z, np.ndarray):
        if z.size == 0:
            return []
        result = np.round(z * (1 - z), 4)
        if z.ndim == 0:
            return float(result)
        elif z.ndim == 1:
            return list(result)
        else:
            return [list(row) for row in result]
    elif isinstance(z, list):
        return _process(z)
    else:
        return _process(z)

class Node:

    def __init__(self):
        self.label = None
        self.x = None
        self.s = None
        self.y = None
        self.child = []
def predict_sample(root: Node, features: list | tuple, task_type: str):
    """Return the prediction for one sample by traversing the decision tree.

    Args:
        root (Node): The root node of the decision tree.
        features (list | tuple): The feature values of the sample.
        task_type (str): Either 'classification' or 'regression'.

    Returns:
        The value stored in the reached leaf node.
    """
    if root.y is not None:
        return root.y
    if task_type == 'classification':
        feature_value = features[root.label]
        for (i, child) in enumerate(root.child):
            if child.x == feature_value:
                return predict_sample(child, features, task_type)
        return predict_sample(root.child[1], features, task_type)
    elif task_type == 'regression':
        feature_value = features[root.label]
        if feature_value <= root.s:
            return predict_sample(root.child[0], features, task_type)
        else:
            return predict_sample(root.child[1], features, task_type)

import math
from collections import defaultdict
import numpy as np
def maxent_predict(X_train: np.ndarray, y_train: np.ndarray, X_test: np.ndarray, epsilon: float=0.001, n_iter: int=100) -> list[int]:
    """MaxEnt classifier using Generalised Iterative Scaling (GIS)."""
    (n_samples, n_features) = X_train.shape
    labels = np.unique(y_train)
    n_labels = len(labels)
    feature_index = 0
    feature_dict = dict()
    index_to_feature = dict()
    for feat_idx in range(n_features):
        unique_values = np.unique(X_train[:, feat_idx])
        for val in unique_values:
            for label in labels:
                key = (feat_idx, val, label)
                if key not in feature_dict:
                    feature_dict[key] = feature_index
                    index_to_feature[feature_index] = key
                    feature_index += 1
    n_features_total = len(feature_dict)
    weights = np.zeros(n_features_total)
    label_counts = defaultdict(int)
    sample_features = []
    for sample_idx in range(n_samples):
        x = X_train[sample_idx]
        y = y_train[sample_idx]
        label_counts[y] += 1
        feats = []
        for feat_idx in range(n_features):
            val = x[feat_idx]
            if isinstance(val, (int, float)):
                val = str(val)
            if isinstance(val, str):
                val = val.encode('utf-8').decode('utf-8')
            key = (feat_idx, val, y)
            if key in feature_dict:
                index = feature_dict[key]
                feats.append(index)
        sample_features.append(feats)

    def indicator(sample_idx, feat_idx, val, y):
        x = X_train[sample_idx]
        actual_val = x[feat_idx]
        if isinstance(val, str):
            val = val.encode('utf-8').decode('utf-8')
        if isinstance(actual_val, str):
            actual_val = actual_val.encode('utf-8').decode('utf-8')
        return int(val == actual_val and y_train[sample_idx] == y)

    def compute_expectations(samples, weights):
        expectations = defaultdict(float)
        partition = np.zeros(n_samples)
        for sample_idx in range(n_samples):
            x = X_train[sample_idx]
            y_candidate = set(labels)
            total = 0.0
            feature_counts = defaultdict(int)
            for y in y_candidate:
                accum = 0.0
                for feat_idx in range(n_features):
                    val = x[feat_idx]
                    key = (feat_idx, val, y)
                    if key in feature_dict:
                        accum += weights[feature_dict[key]]
                feature_counts[y] = accum
                total += math.exp(accum)
                partition[sample_idx] += total
            for y in y_candidate:
                prob = math.exp(feature_counts[y]) / total
                for feat_idx in range(n_features):
                    val = x[feat_idx]
                    if isinstance(val, str):
                        val = val.encode('utf-8').decode('utf-8')
                    key = (feat_idx, val, y)
                    if key in feature_dict:
                        feat_index = feature_dict[key]
                        expectations[feat_index] += prob
        return expectations

    def compute_empirical_expectations():
        empirical = defaultdict(float)
        for sample_idx in range(n_samples):
            for y in labels:
                for feat_idx in range(n_features):
                    val = X_train[sample_idx, feat_idx]
                    if isinstance(val, str):
                        val = val.encode('utf-8').decode('utf-8')
                    key = (feat_idx, val, y)
                    if key in feature_dict:
                        empirical[feature_dict[key]] += indicator(sample_idx, feat_idx, val, y)
        for (idx, count) in empirical.items():
            empirical[idx] /= n_samples
        return empirical
    empirical_expectations = compute_empirical_expectations()
    for iteration in range(n_iter):
        expectations = compute_expectations(X_train, weights)
        update = np.zeros(n_features_total)
        for idx in range(n_features_total):
            update[idx] = math.log(empirical_expectations[idx] / expectations[idx])
        weights += update
        max_update = np.max(np.abs(update))
        if max_update < epsilon:
            break
    predictions = []
    for x in X_test:
        label_probs = {}
        for label in labels:
            score = 0.0
            for feat_idx in range(n_features):
                val = x[feat_idx]
                if isinstance(val, str):
                    val = val.encode('utf-8').decode('utf-8')
                key = (feat_idx, val, label)
                if key in feature_dict:
                    index = feature_dict[key]
                    score += weights[index]
            label_probs[label] = score
        predicted_label = max(label_probs, key=label_probs.get)
        predictions.append(int(predicted_label))
    return predictions
