def matrix_dot_vector(a: list[list[int | float]], b: list[int | float]) -> list[int | float]:
    if not a or not a[0]:
        return -1
    rows = len(a)
    cols = len(a[0])
    if cols != len(b):
        return -1
    result = []
    for i in range(rows):
        if len(a[i]) != cols:
            return -1
        dot_product = 0
        for j in range(cols):
            dot_product += a[i][j] * b[j]
        result.append(dot_product)
    return result
