def transform_matrix(A: list[list[int | float]], T: list[list[int | float]], S: list[list[int | float]]) -> list[list[int | float]]:
    try:
        A = np.array(A, dtype=float)
        T = np.array(T, dtype=float)
        S = np.array(S, dtype=float)
        if T.shape[0] != T.shape[1] or S.shape[0] != S.shape[1]:
            return -1
        if A.shape[0] != T.shape[0] or A.shape[1] != S.shape[0]:
            return -1
        if abs(np.linalg.det(T)) < 1e-10 or abs(np.linalg.det(S)) < 1e-10:
            return -1
        T_inv = np.linalg.inv(T)
        result = np.matmul(np.matmul(T_inv, A), S)
        result = np.round(result, decimals=4)
        return result.tolist()
    except Exception as e:
        return -1
