def mean(vector: list[float]) -> float:
    """Calculate the mean of a vector."""
    return sum(vector) / len(vector)
def covariance(vector1: list[float], vector2: list[float]) -> float:
    """Calculate covariance between two vectors."""
    if len(vector1) != len(vector2):
        raise ValueError('Vectors must have the same length')
    mean1 = mean(vector1)
    mean2 = mean(vector2)
    sum_of_products = sum(((x - mean1) * (y - mean2) for (x, y) in zip(vector1, vector2)))
    return sum_of_products / (len(vector1) - 1)
def calculate_covariance_matrix(vectors: list[list[float]]) -> list[list[float]]:
    """
    Calculate the covariance matrix for a given set of vectors.

    Args:
        vectors: A list of lists where each inner list contains observations for a feature

    Returns:
        A covariance matrix as a list of lists
    """
    if not vectors or not vectors[0]:
        raise ValueError('Input vectors cannot be empty')
    n = len(vectors[0])
    if not all((len(v) == n for v in vectors)):
        raise ValueError('All vectors must have the same length')
    num_features = len(vectors)
    cov_matrix = [[0.0] * num_features for _ in range(num_features)]
    for i in range(num_features):
        for j in range(num_features):
            cov_matrix[i][j] = covariance(vectors[i], vectors[j])
    return cov_matrix
