from collections import Counter
def entropy(examples: list[dict], target_attr: str) -> float:
    """Calculate the entropy of a set of examples based on target attribute."""
    if not examples:
        return 0
    target_counts = Counter((example[target_attr] for example in examples))
    total = len(examples)
    return -sum((count / total * math.log2(count / total) for count in target_counts.values()))
def information_gain(examples: list[dict], attribute: str, target_attr: str) -> float:
    """Calculate information gain for an attribute."""
    current_entropy = entropy(examples, target_attr)
    grouped = {}
    for example in examples:
        value = example[attribute]
        if value not in grouped:
            grouped[value] = []
        grouped[value].append(example)
    weighted_entropy = sum((len(group) / len(examples) * entropy(group, target_attr) for group in grouped.values()))
    return current_entropy - weighted_entropy
def plurality_value(examples: list[dict], target_attr: str) -> str:
    """Return the most common target value in examples."""
    if not examples:
        return None
    return Counter((example[target_attr] for example in examples)).most_common(1)[0][0]
def all_examples_same_classification(examples: list[dict], target_attr: str) -> bool:
    """Check if all examples have the same target value."""
    if not examples:
        return True
    first_value = examples[0][target_attr]
    return all((example[target_attr] == first_value for example in examples))
def learn_decision_tree(examples: list[dict], attributes: list[str], target_attr: str) -> dict:
    """
    Learn a decision tree from examples using the given attributes.
    Returns a nested dictionary representing the decision tree.
    """
    if not examples:
        return {'type': 'leaf', 'value': plurality_value(examples, target_attr)}
    if all_examples_same_classification(examples, target_attr):
        return {'type': 'leaf', 'value': examples[0][target_attr]}
    if not attributes:
        return {'type': 'leaf', 'value': plurality_value(examples, target_attr)}
    gains = {attr: information_gain(examples, attr, target_attr) for attr in attributes if attr != target_attr}
    if not gains:
        return {'type': 'leaf', 'value': plurality_value(examples, target_attr)}
    best_attribute = max(gains.items(), key=lambda x: x[1])[0]
    tree = {'type': 'node', 'attribute': best_attribute, 'branches': {}}
    for value in set((example[best_attribute] for example in examples)):
        subset = [example for example in examples if example[best_attribute] == value]
        remaining_attributes = [attr for attr in attributes if attr != best_attribute]
        subtree = learn_decision_tree(subset, remaining_attributes, target_attr)
        tree['branches'][value] = subtree
    return tree
