def rref(matrix):
    A = np.array(matrix, dtype=float)
    (rows, cols) = A.shape
    lead = 0
    for r in range(rows):
        if lead >= cols:
            break
        i = r
        while i < rows and A[i][lead] == 0:
            i += 1
        if i == rows:
            lead += 1
            if lead < cols:
                r -= 1
            continue
        if i != r:
            (A[r], A[i]) = (A[i].copy(), A[r].copy())
        pivot = A[r][lead]
        if pivot != 0:
            A[r] = A[r] / pivot
        for i in range(rows):
            if i != r:
                factor = A[i][lead]
                A[i] = A[i] - factor * A[r]
        lead += 1
    A[np.abs(A) < 1e-10] = 0
    return A.tolist()
