def compute_tf_idf(corpus, query):
    if not corpus:
        raise ValueError('Corpus cannot be empty')
    if not query:
        return [[0.0] for _ in corpus]
    N = len(corpus)

    def compute_tf(term, document):
        """Compute term frequency"""
        if not document:
            return 0.0
        count = Counter(document)
        return count.get(term, 0) / len(document)

    def compute_idf(term):
        """Compute inverse document frequency with smoothing"""
        doc_count = sum((1 for doc in corpus if term in set(doc)))
        return math.log((N + 1) / (doc_count + 1)) + 1
    result = np.zeros((len(corpus), len(query)))
    for (i, document) in enumerate(corpus):
        for (j, term) in enumerate(query):
            tf = compute_tf(term, document)
            idf = compute_idf(term)
            result[i, j] = tf * idf
    result = np.round(result, 4)
    return result.tolist()
