import numpy as np
def create_row_hv(row, dim, random_seeds):
    """
    Creates a composite hypervector for a dataset row using HDC principles.

    Args:
        row (dict): Dictionary containing feature names and their values
        dim (int): Dimensionality of the hypervectors
        random_seeds (dict): Dictionary of random seeds for each feature

    Returns:
        list: Composite hypervector representing the entire row
    """

    def generate_random_hv(seed, dim):
        """Generate a random bipolar hypervector (-1 or 1) using a specific seed"""
        np.random.seed(seed)
        return np.random.choice([-1, 1], dim)

    def bind_hvs(hv1, hv2):
        """Bind two hypervectors using element-wise multiplication"""
        return hv1 * hv2

    def bundle_hvs(hvs):
        """Bundle multiple hypervectors using element-wise addition and thresholding"""
        summed = np.sum(hvs, axis=0)
        return np.sign(summed)

    def normalize_value(value):
        """Normalize value to be used as part of the seed"""
        if isinstance(value, (int, float)):
            return int(value * 1000)
        return hash(str(value))
    bound_hvs = []
    for (feature_name, value) in row.items():
        feature_hv = generate_random_hv(hash(feature_name), dim)
        value_seed = random_seeds[feature_name] + normalize_value(value)
        value_hv = generate_random_hv(value_seed, dim)
        bound_hv = bind_hvs(feature_hv, value_hv)
        bound_hvs.append(bound_hv)
    composite_hv = bundle_hvs(bound_hvs)
    return composite_hv.tolist()
