        if context_length >= hparams['n_ctx']:
            token_ids = token_ids[-hparams['n_ctx']:]
            context_length = hparams['n_ctx']
        token_embeddings = params['wte'][token_ids]
        position_embeddings = params['wpe'][:context_length]
        h = token_embeddings + position_embeddings
        h = layer_norm(h, params['ln_f']['g'], params['ln_f']['b'])
        last_token_embedding = h[-1]
        logits = np.dot(last_token_embedding, params['wte'].T)
        next_token = np.argmax(logits)
        token_ids.append(next_token)
    generated_text = encoder.decode(token_ids)
    return generated_text
