    alpha = 1.6732632423543772
    scale = 1.0507009873554805
    try:
        if x >= 0:
            result = scale * x
        else:
            if x < -88:
                exp_x = 0
            else:
                exp_x = math.exp(x)
            result = scale * alpha * (exp_x - 1)
        return round(result, 4)
    except OverflowError:
        if x > 709:
            return round(scale * x, 4)
        return 0.0
    except Exception as e:
