def matrix_dot_vector(a: list[list[int | float]], b: list[int | float]) -> list[int | float]:
    if not a:
        num_cols = 0
    else:
        num_cols = len(a[0])
    if len(b) != num_cols:
        return -1
    result = []
    for row in a:
        dot_product = 0
        for i in range(num_cols):
            dot_product += row[i] * b[i]
        result.append(dot_product)
    return result
